	function pgScreenSize(){
		var getWidth = $(window).width();

		if (getWidth >= 1024) { //Desktop View
        	$('#featured-ue-cont').attr('data-closed-height', '360');
            ueRegionInit();

			multimediaRotator();
            setTimeout(function(){
            	$('#sw-content-container11').css('height', $('#sw-content-container10').outerHeight());
			}, 250); //this timeout seems to allow the MMR script to fully process before we resize; this could be a point-of-failure if clock speeds are slower and 150ms is not enough of a timeout; the odds are rare though.
		}else{ //Mobile BPs
        	$('#featured-ue-cont').attr('data-closed-height', '65');
        	ueRegionInit();

        	multimediaRotator();
            $('#sw-content-container11').css('height', '350px');
			appView();
		}
	}

	function multimediaRotator(){
		if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length){
			// MULTIMEDIA ROTATOR
            var transSpeed = (3 < 4) ? 4 : 3;

			$("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
				"imageWidth"              : 800,
                "imageHeight"             : 500,
                "efficientLoad"           : "yes",
                "appHeader"               : "no",
                "titles"                  : "yes",
                "captions"                : "yes",
                "galleryOverlay"          : "no",
                "linkedElement"           : ["title"], // ["image", "title", "overlay", "all"]
                "playPause"               : "no",
                "mainControls"            : "yes",
                "contMoreLink"            : "no",
                "contMoreLinkText"        : "Read More",
                "descMoreLink"            : "no",
                "descMoreLinkText"        : "Read More",
                "bullets"                 : "yes",
                "bulletControls"          : "no",
                "thumbnails"              : "no",
                "thumbnailWidth"          : 122,
                "thumbnailHeight"         : 53,
                "thumbnailSlideDirection" : "horizontal", // horizontal or vertical
                "hoverPause"              : "yes",
                "autoRotate"              : "yes",
                "fullScreenRotator"       : "no", // image dimensions work well at 1500x915
                "fullScreenBreakpoints"   : ["960", "768", "640", "480", "320"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
                "fullScreenVideo"         : "no",
                "transitionType"          : 'fade', // fade or slide
                "transitionSpeed"         : 3,
                "transitionDelay"         : 20,//transSpeed,
                "onImageLoad"             : function() { },
                "allImagesLoaded"         : function() { },
                "onThumbnailLoad"         : function() { },
                "allThumbnailsLoaded"     : function() { },
                "onTransitionStart"       : function() { },
                "onTransitionEnd"         : function() { },
                "watchVidClick"           : function() { },
                "closeVidClick"           : function() { },
                "allLoaded"               : function() { 

                    //$('#sw-content-container11').css('max-height', $('#sw-content-container10').height());

                	$('#hp-slideshow .rs-gallery-container').append('
');

                    $('#hp-slideshow .rs-gallery-desc').prependTo('#hp-slideshow .cs-gallery-bottom-cont');

                    if( $('#hp-slideshow .rs-gallery-control.next').length > 0 ){
                    	$('#hp-slideshow .cs-gallery-bottom-cont').append('
');
                    	$('#hp-slideshow .rs-gallery-control.back').appendTo('#hp-slideshow .cs-gallery-controls-cont');
                        $('#hp-slideshow .rs-gallery-control.next').appendTo('#hp-slideshow .cs-gallery-controls-cont');
                    }

                    if( $('#hp-slideshow .rs-gallery-bullets-outer').length > 0 ){
                    	if( $('#hp-slideshow .cs-gallery-bottom-cont').length <= 0 ){
                        	$('#hp-slideshow .cs-gallery-bottom-cont').append('
');
                        }
                    	$('#hp-slideshow .rs-gallery-bullets-outer').appendTo('#hp-slideshow .cs-gallery-controls-cont');
                    }
                }
			});
		}
	}

	function modEvents(){
	    if (!$(".upcomingevents .joel-month").length) {
	        $(".upcomingevents").modEvents({
	            columns     : "yes",
	            monthLong   : "yes",
	            dayWeek     : "yes"
	        });
	        eventsByDay(".upcomingevents .ui-articles");
	    }
	}

	function eventsByDay(container) {
	    var countLi = 0;
	    $(".ui-article", container).each(function(){
	        if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
	            var moveArticle = $(this).html();
	            $(this).parent().prev().children().children().next().append(moveArticle);
	            $(this).parent().remove();
	        };
	    });

        var months = {
        	"january": "jan",
            "february": "feb",
            "march": "mar",
            "april": "apr",
            "may": "may",
            "june": "june",
            "july": "july",
            "august": "aug",
            "september": "sept",
            "october": "oct",
            "november": "nov",
            "december": "dec"
        };
        var weekDay = [];

        $(".ui-article", container).each(function(){
			var newDateTime = $('.upcoming-column.left h1', this).html().toLowerCase().split("");
			var newDate = '';
			var dateIndex = ((newDateTime.length > 2) ? 2 : 1); //if the dayWeek is set to yes we need to account for that in our array indexing

			//add a zero to the date if it is a single digit
			if( newDateTime[dateIndex].length == 1 ) newDateTime[dateIndex] = "0" + newDateTime[dateIndex];

			//add in the month
            weekDay = newDateTime[dateIndex - 1].split('>');
			newDate += '' + months[weekDay[1]] + ''; //the month is always the in the left array position to the date

			//wrap the date in a new tag
			newDate += '' + newDateTime[dateIndex] + '';

            //if we have the day of the week make sure to include it.
			if( dateIndex > 1 ){ 
            	weekDay = newDateTime[0].split('>');
				newDate += '' + weekDay[1] + '';
			}

			//append the date and month back into their columns
			$('.upcoming-column.left h1', this).html(newDate);	

			//add an ALL DAY label if no time was given
			$('.upcoming-column.right .ui-article-description', this).each(function(){
				if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
					$(this).prepend('ALL DAY');
				}else{ //remove the :00 from the date
                    var time = $('.sw-calendar-block-time', this).html().split(' - ');
                    var newTimeOne = time[0];
                    var newTimeTwo = time[1];

                    if( time[0].indexOf(':00') >= 0 ){
                        newTimeOne = time[0].split(':');
						newTimeOne = newTimeOne[0] + time[0].split(' ')[1];
                    }

                    if( time[1] != undefined ){
                        if( time[1].indexOf(':00') >= 0 ){
                            newTimeTwo = time[1].split(':');
                            newTimeTwo = newTimeTwo[0] + time[1].split(' ')[1];
                        }

                        $('.sw-calendar-block-time', this).html(newTimeOne + " - " + newTimeTwo);
                    }
                }

			});
		});
	}

	function appView(){
		//DOC dont add app into accordion if it doesnt have a header
		$("#hp-content div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}
		});

        $('.accordion-view').each(function(k, v){
        	$("div.ui-widget-header", v).off('click').click(function(){
                if( $(this).parent().hasClass("active") ){ //If they click the same header again it will hide
                    $(this).parent().removeClass("active");
                }else{
                    var clickedApp = $(this).parent().parent().attr("id");
                    $("#hp-mixed-content div.ui-widget.app.active").removeClass("active"); 
                    $(this).parent().addClass("active");
                    loadAppScroller();
                    smoothScroll("#" + clickedApp, 10);
                }
            });
        });


        $('#hp-content #featured-ue-cont div.ui-widget-header').click(function(e){
        	if( $('#hp-featured-ue').hasClass('active') ){
            	$('#hp-featured-ue').removeClass('active');
            }else{
            	$('#hp-featured-ue').addClass('active');
            }
        	$('#ue-expand-button').click();
        });
	}	

