    var closedUeH = 0;
    var openUeH = 0;
    function ueRegionInit(){
    	if( $('#featured-ue-cont .region').children().length > 0 ){
            closedUeH = $('#featured-ue-cont').attr('data-closed-height'); //set our closed value first

            $('#featured-ue-cont').css({ //clear out all height assignments so we can get a clean read
                'max-height': 'none',
                'height': 'auto'
            });
            $('#featured-ue-cont')
                .attr('data-open-height', $('#hp-featured-ue').height()) //get a clean read and add it to the data-attribute
                .css('height', closedUeH); //reset the height for animation purposes

            openUeH = $('#featured-ue-cont').attr('data-open-height'); //set our open value variable


            if( $('#hp-featured-ue #featured-ue-cont').hasClass('expanded-area') ){
                //match the heights
                $('#hp-featured-ue #featured-ue-cont').css('height', 'auto');
            }else{
                //check if we should be running
                if( !$('#hp-featured-ue').hasClass('expandable-content') ){
                        //add the appropriate markup to the container
                    $('#hp-featured-ue').addClass('expandable-content');
                        //add the button to the container
                    $('#hp-featured-ue').append('Expand Events ');
                        //stop tabbing into a tags that may be hidden
                    $('#hp-featured-ue a').attr('tabindex', -1);

                        //create the click event for the button
                    $('#hp-featured-ue #ue-expand-button').click(function(e){
                        if( $(this).hasClass('expanded-area') ){

                            $(this).attr('aria-expanded', 'false').removeClass('expanded-area');
                            $('.label', this).html('Expand Events');
                            $('i', this).addClass('point-down').removeClass('point-up');

                            $('#featured-ue-cont').animate({ //animate the height back to the default
                                'height': closedUeH
                            }, {
                                'duration': 400,
                                'complete': function(){
                                    $('#featured-ue-cont').removeClass('expanded-area');
                                }
                            });

                            smoothScroll('#hp-featured-ue', $('#sticky-header').height() + 20, 400);

                            $('#featured-ue-cont').attr('aria-hidden', 'true');
                            $('#featured-ue-cont a').attr('tabindex', -1);
                            $('#hp-featured-ue').removeClass('active'); //this is here solely for mobile, but it can't hurt

                        }else{

                            $(this).attr('aria-expanded', 'true').addClass('expanded-area');

                            $('#featured-ue-cont').addClass('expanded-area'); //this has to happen before the animation just in case we need to overwrite any inline max-height values.

                            $('.label', this).html('Collapse Events');

                            $('i', this).addClass('point-up').removeClass('point-down');

                            $('#featured-ue-cont').attr('aria-hidden', 'false');

                            $('#featured-ue-cont a').attr('tabindex', 0);

                            $('#featured-ue-cont').animate({ //animate it
                                'height': openUeH
                            }, 400);

                            if( $(window).width >= 1024 ){
                                $('#hp-featured-ue .ui-articles > li:first .ui-article-description:first .sw-calendar-block-title a').focus(); //auto focus the first 
                            }
                        }

                    });
                }else if( $('#hp-featured-ue #feature-ue-cont').height() > 0 ){
                    $('#hp-featured-ue').removeClass('expandable-content');
                    $('#hp-featured-ue #featured-ue-cont').attr('aria-hidden', '');
                    $('#hp-featured-ue #ue-expand-button').remove();
                }
            }
        }else{
        	$('#hp-featured-ue').remove();
        }
    }

    var headlineScrollerInterval = {};
    var headlineScrollerIntervalTime = 7 * 1000;
    function setupAutoRotatingHeadlines(){
    	if( $('#hp-headline-scroller .headlines-scroll-ctrl.next').length > 0 && $(window).width() >= 1024 ){

            if( !$('#hp-headline-scroller').hasClass('rotating') ){
            	$('#hp-headline-scroller').addClass('rotating');
                clearInterval(headlineScrollerInterval);
                headlineScrollerInterval = setInterval(function(e){
                    $('#hp-headline-scroller .headlines-scroll-ctrl.next').trigger('click');
                }, headlineScrollerIntervalTime);
            }

            $('#hp-headline-scroller')
                .off('hover')
                .hover(function(e){
                	$('#hp-headline-scroller').removeClass('rotating');
					clearInterval(headlineScrollerInterval);
                },
                function(e){
                	$('#hp-headline-scroller').addClass('rotating');
                    clearInterval(headlineScrollerInterval);
					headlineScrollerInterval = setInterval(function(e){
                        $('#hp-headline-scroller .headlines-scroll-ctrl.next').trigger('click');
                    }, headlineScrollerIntervalTime);
                });
        }else{
        	clearInterval(headlineScrollerInterval);
        	$('#hp-headline-scroller').removeClass('rotating').off('hover');
        }
    }


	function searchText() {
		var defSearchText = "SEARCH SOMETHING";

	    var searchBox = $('#swsitesearch-txt-searchstring');
        searchBox
			.attr("aria-label", "Search input")
			.attr("placeholder", '')
			.val(defSearchText)
			.focus(function() {
                if($(this).val() == defSearchText) {
                    $(this).val("");
                }

                searchBox.off('keyup keydown keypress').on('keydown', function(e) { // Trigger the click event from the keyboard
                    var code = e.which;
                    // 13 = Return, 32 = Space
                    if ((code === 9) ) {
                        e.preventDefault();
                        $('#gb-search-trigger').focus();
                    }
                    //if statement below for code === 13 added by Lyndsay - 12.15.16 - Case 00442194
                    if ((code === 13) ) {
                        e.preventDefault();
                        SWGoToSearchResultsPageswsitesearchtxtsearchstring();
                    }
                });
            })
            .blur(function() {
                if($(this).val() == "") {
                    $(this).val(defSearchText);
                }
                searchBox.off('keydown keyup keypress');
            });
	}

	function calendarListView(){
		//DOC auto click function for schoolwires calendar list view for small mobile devices
		if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
			$("a.ui-btn-toggle.list").click();
			$("a.ui-btn-toggle.month").click();
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
		}

		var getWidth = $(window).width();
		if(getWidth >= 640){ // 640+
			$(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
		} if(getWidth < 640){ // 480-
			$(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
			$(".ui-btn-toggle.month").removeClass("active");
			$(".ui-btn-toggle.list").addClass("active");
		}
	}


    function clearDropdowns(){
    	$('.custom-dropdown.openMenu input, .custom-dropdown.openMenu a, .custom-dropdown.openMenu .custom-dropdown > button').attr('tabindex', -1);
        $('.custom-dropdown.openMenu .dropdown').attr('aria-expanded', 'false').attr('aria-hidden', 'true').slideUp();
        $('.custom-dropdown.openMenu .dropdown-icon').removeClass('point-up').addClass('point-right');
        $('.custom-dropdown.openMenu > button').removeClass('acc-open').attr('aria-expanded', 'false');
        $('.custom-dropdown.openMenu li').removeClass('active-list-item');
		dropdownKeyboardFunctionality($('.custom-dropdown.openMenu .dropdown'), true);
		$('.custom-dropdown.openMenu').removeClass("openMenu");
    }

    function dropdownKeyboardFunctionality(dropdown, clear){
    	if( clear ){
        	dropdown.off('keydown');
        }else{
        	$('input, a', dropdown).attr('tabindex', 0);
	        $('li:first', dropdown).addClass('active-list-item');
        	$('li.active-list-item a', dropdown).trigger('focus');

            	// 38 = up and 40 = down
        	dropdown.keydown(function(e){
            	if(e.which == 38 || e.which == 40){
                	e.preventDefault();
                    switch( e.which ){
                        case 38:  //up
							if( $('li.active-list-item', this).index() == 0){
                            	$(this).siblings('button').focus();
                                $('li', this).removeClass('active-list-item');
                                $('li:first', this).addClass('active-list-item');
                            }else{	
                            	var topItem = $('li.active-list-item', this).prev('li');
                                if(topItem.hasClass('list-label')) topItem = topItem.prev('li');

                                $('li.active-list-item', this).removeClass('active-list-item');
                                topItem.addClass('active-list-item');
                                $('a', topItem).focus();
                            }
                            break;
                        case 40:  //down
                        	if( $('li.active-list-item', this).index() == $('li', this).length - 1 ){
                                $('li', this).removeClass('active-list-item');
                                $('li:first', this).addClass('active-list-item');
                                $('li.active-list-item a', this).focus();
                            }else{
                            	var bottomItem = $('li.active-list-item', this).next('li');
                                if(bottomItem.hasClass('list-label')) bottomItem = bottomItem.next('li');

                                $('li.active-list-item', this).removeClass('active-list-item');
                                $(bottomItem).addClass('active-list-item');
                                $('a', bottomItem).focus();
                            }
                            break;
                        default:
                            //pass
                            break;
                    }
                }
            });
        }
    }

	/* customDropdowns
	**  Use: Sets up custom dropdown menus.
	**  Param: openIcon, closedIcon -- The classes that you want to mark the on/off state of the menu icons (dont pass the . or #)
	*/
    var dropdownTimeout = ""; //Will hold our timeout object that will ge t passed among the dropdown functions.
	function customDropdowns(openIcon, closedIcon){
        $('.custom-dropdown input, .custom-dropdown a, .custom-dropdown .custom-dropdown > button').attr('tabindex', -1);
        $('.custom-dropdown').on('keydown', function(e) { // Trigger the click event from the keyboard
            var code = e.which;
            // 13 = Return, 32 = Space
           	if ((code === 13) || (code === 32)) {
                e.preventDefault();

                $(this).click();
                if( !$(this).parent().hasClass('openMenu') ){
                	//clear our keyboard hooks since the menu is closing.
                	dropdownKeyboardFunctionality($('~ .dropdown', this), true);
                }else{
                	//add our keyboard hooks
               		dropdownKeyboardFunctionality($('~ .dropdown', this));
                }


            }
        });

		//Custom Dropdown Functionality
		$('.custom-dropdown').off('click').click(function(e){
			clearTimeout(dropdownTimeout);

			if( $('> button', this).attr("aria-expanded") == "false" ){
				clearDropdowns();
				$('.dropdown', this).attr('aria-expanded', 'true').attr('aria-hidden', 'false').slideDown();
                $('> button', this).attr('aria-expanded', 'true');
				$(this).addClass("openMenu");
                $('.custom-dropdown.openMenu input, .custom-dropdown.openMenu a, .custom-dropdown.openMenu .custom-dropdown > button').attr('tabindex', 0);
				$('.dropdown-icon', this).removeClass(closedIcon).addClass(openIcon);
			}else{
            	clearDropdowns();
			}
		})
		.hover(function(e){ //In
			clearTimeout(dropdownTimeout);
		},function(e){ //Out
			//canDropDownHide = true;
			dropdownTimeout = setTimeout(function(){
            	clearDropdowns();
			}, 800);
		});
	}

	/*
	** smoothScroll
	** Use: This will smoothly scroll to the id that was passed to the function.
	** Return: N/A
	** Parameter(s): to (jQuery "#id") The id of the section to be scolled to.
	** 		offset (int) The amount of space you want to be left on the top of the element after scroll (in a whole number int value)
	*/
	function smoothScroll( to, offset, duration ){
	    if( to.indexOf("#") < 0 ){ to = "#" + to; } //makes sure we have a valid id accessor string
	    var target = $(to);
        var timer = (duration != undefined) ? duration : 800;

	    if (target.length) {
	        $('html,body').animate({
	            scrollTop: target.offset().top - offset //this offset accounts for the fixed menu on the top
	        }, timer, function(){
	            $("html, body").stop(true, false);
	        });
	        return false;
	    }
	}

    function setupCustomColors(){

		var dynStyleSheet = document.createElement('style');

		if(dynStyleSheet) {
			dynStyleSheet.setAttribute('type', 'text/css');
			var head = document.getElementsByTagName('head')[0];

			if(head) {
				head.insertBefore(dynStyleSheet, head.childNodes[0]);
			}

			var dynStyles = "#gb-channel-list-outer, .sticky-channel-bar #gb-channel-list { background: rgba(" + hexToRgb('#2257a5') + ", 0.8); }" +
            				"#hp-slideshow-outer:before { background: rgba(" + hexToRgb('#fdb626') + ", 0.5); }" +
                            "#hp-slideshow .rs-gallery-bottom-control-inner { background: rgba(" + hexToRgb('#2257a5') + ", 0.8); }" +
                            //"#gb-channel-list-outer:before { : solid 12px rgba(" + hexToRgb('#fdb626') + ", 0.5); }" +
                            "#hp-slideshow .rs-photo-gallery-desc{ background: rgba(" + hexToRgb('#2257a5') + ", 0.8);}" +
                            "#gb-sidebar-nav .sidebar-item .sidebar-slideout .slideout-inner{ background: rgba(" + hexToRgb('#2257a5') + ", 0.8);}" +
                            "#gb-sidebar-nav .sidebar-item .sidebar-slideout.search-container{ background: rgba(" + hexToRgb('#2257a5') + ", 0.8);}";

			var rules = document.createTextNode(dynStyles);

			if(dynStyleSheet.styleSheet){ // IE
				dynStyleSheet.styleSheet.cssText = dynStyles;
			} else {
				dynStyleSheet.appendChild(rules);
			}
		}
	}

    function hexToRgb(h){
	        var r = parseInt((cutHex(h)).substring(0,2),16);
	        var g = parseInt((cutHex(h)).substring(2,4),16);
	        var b = parseInt((cutHex(h)).substring(4,6),16);
	        return r+', '+g+', '+b;
	}

	function cutHex(h) {
	        return (h.charAt(0)=="#") ? h.substring(1,7):h;
	}

    /*stickyHeader
		Use: It handles the sticky header that will make the header become fixed upon scrolling
		Parameter: shutoff -- this is a numeric value in which the width of the screen will stop the sticky header from activating.
	*/
	var csNavIsSticky = 0;
	function stickyHeader(shutoff, scroll){
		var getWidth = $(window).width();

		if (getWidth >= 1024) {
			// CHANNEL BAR
			var gbNavOffSet = $("#sticky-header-outer").offset().top;

			if ($(window).scrollTop() <= gbNavOffSet) {
				if(csNavIsSticky) {
					$("#sticky-header, #gb-page").removeClass("on");
					$("#sticky-header-outer").css("height", "auto");
					csNavIsSticky = 0;
				}
			} else {
				$("#sticky-header-outer").css("height", $("#sticky-header").outerHeight());				
				if(!csNavIsSticky) {
					$("#sticky-header, #gb-page").addClass("on");
					csNavIsSticky = 1;
				}
			}

            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                if($(".wcm-controls").hasClass("wcm-stuck")) {
                    $(".wcm-controls").css("margin-top", $("#sticky-header").outerHeight(true));
                }
            }
		} else {
			if(csNavIsSticky == 1) {
				$("#sticky-header").removeClass("on");
				$("#sticky-header-outer").css("height", "auto");

				csNavIsSticky = 0;
			}
		}
	}

