$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=2VCZ6F32xGSj56rj%2fXdTsQ%3d%3d&SessionID=ebd5769c-9405-4e85-b9a7-d9b9a001e0b1&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f26%2f2020+5%3a37%3a41+PM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.11.12';
   try {
      $.getJSON(beaconURL + '&jsonp=?', function(myData) {});
   } catch(err) { 
      // prevent site error for analytics
   }
