			function showData(jsondata) { 
				var s = jsondata.d.results[0];
				var hitDataSection = false;
				var dataSectionTrigger;
				
				$( window ).resize(function() {
					dataSectionTrigger = $( "#tps-home-data-points" ).offset().top - $( "#s4-workspace" ).height() + 200;
				});


				$('#s4-workspace').scroll(function() { // using s4-workspace insead of window because SP still breaks normal scrolling 
				    if ($('#s4-workspace').scrollTop() > dataSectionTrigger ){
				    	if ( hitDataSection == false ){
							$( "#tps-home-data-point1").animateNumber({ number: s.innovative });
							$( ".tps-home-data-point:nth-of-type(1)").addClass("tps-home-data-point-slide1");
							$( "#tps-home-data-point2").animateNumber({ number: s.gradRate,     easing: 'easeInQuad', },1400); /* the ".6%" added to the number in html below to make the animateNumber plugin work as expected */
							$( ".tps-home-data-point:nth-of-type(2)").addClass("tps-home-data-point-slide2");
							$( "#tps-home-data-point3").animateNumber({ number: s.volunteers,   easing: 'easeInQuad', },1600);
							$( ".tps-home-data-point:nth-of-type(3)").addClass("tps-home-data-point-slide3");
							$( "#tps-home-data-point4").animateNumber({ number: s.preschoolers, easing: 'easeInQuad', },1800);
