			var newline = /\n/g; // the /g is for global so it matches every instance of the pattern
			var hashtag = /#(\w+)/g; // w+ matches a whole word
			var user = /@(\w+)/g;

			function formatCaption(c,d){ // takes the caption text and date and returns a formatted caption with html for line breaks, links to tags and links to Instagram users
			  var newCaption = c;
			  d = new Date(d * 1000);
			  d = $.timeago(d);
			  newCaption = newCaption.replace(newline, '
');
			  newCaption = newCaption.replace(hashtag, '#$1');
			  newCaption = newCaption.replace(user, '@$1');
			  newCaption = newCaption + '

'+ d +' | More from @tacomaschools';
			  return newCaption;
			}


