			var fbPosts = ''; // to hold all the JSON data returned by the main AJAX call

			var a1 = $.ajax({
			  type: 'GET',
			  dataType: 'jsonp',
			  cache: false,
			  url: fbPostsREST,
			  success: function(data) {
			    fbPosts = data;
				var postCount = 0;
				for (var i = 0; i < 100; i++) {
					if (postCount < 3 ) //////////////////////////////// Ultimately, this should be < 3
					{
						if( fbPosts.data[i].type != 'video' && fbPosts.data[i].message && fbPosts.data[i].name && fbPosts.data[i].full_picture && fbPosts.data[i].link && fbPosts.data[i].status_type != 'mobile_status_update' ){ 
						// in the order to knock out the most unwanted posts first.
						// also making sure the data we need below is available - avoids errors due to undefined fields. (type and status_type are always available)

							if ( fbPosts.data[i].name.indexOf("Schools's") < 0 && fbPosts.data[i].name != "Timeline Photos" && fbPosts.data[i].name != "bit.ly" ){ 
							// avoids some common unwanted posts: 
								// "Schools's" is a result of FB automatically creating a name for a post like when PIO updates the cover photo (they'd never write "Tacoma Public Schools's") - these are posts I wanted to exclude anyway.
								// "Timeline Photos" is another automated title - we're just excluding those posts because the title is lame
								// "bit.ly" is a post name that I imagine FB makes automatially when posting a bitly link and not specifying a name for the post. Again, excluding because of lame title.

								postCount++;

								var postMessage = fbPosts.data[i].message;

							    if ( postMessage.length > 399 ){ // Keeping the message to 400 characters and providing a link for more.
							      var lastWord = 397;
							      postMessage = postMessage.substr(0, 397).trim();
							      lastWord = postMessage.lastIndexOf(' ');
							      postMessage = postMessage.substr(0, lastWord);
							      if ( postMessage.slice(-1) == '.' || postMessage.slice(-1) == ',' || postMessage.slice(-1) == '-' ){
							      	postMessage = postMessage.substr(0, postMessage.length -2);
							      }
							      postMessage = postMessage + '...';
							    } 


								var postHTML = 				'
';
								var postHTML = postHTML + 		'
';
								var postHTML = postHTML + 			'';
								var postHTML = postHTML + 				'' + fbPosts.data[i].name + '';
								var postHTML = postHTML + 				'';
								var postHTML = postHTML + 			'';
								var postHTML = postHTML + 			'
' + postMessage + '

';
								var postHTML = postHTML + 				'';
								var postHTML = postHTML + 					$.timeago(fbPosts.data[i].created_time) +' | ';
								var postHTML = postHTML + 					'More from Tacoma Schools';
								var postHTML = postHTML + 				'';
								var postHTML = postHTML + 			'';
								var postHTML = postHTML + 		'';
								var postHTML = postHTML + 	'';

								$('#tps-home-social-facebook').append( postHTML );
							}
						}
					}
				}
			  },
			  error: function( req, status, err ) { console.log( 'something went wrong', status, err ); }
			});




