			function convertToSeconds(input) {
			  var regExPattern = /^PT(?:(\d+)H)?(?:(\d+)M)?(?:(\d+)S)?$/;
			  var hours = 0, minutes = 0, seconds = 0, totalseconds;

			  if (regExPattern.test(input)) {
			    var matches = regExPattern.exec(input);
			    if (matches[1]) hours   = Number(matches[1]);
			    if (matches[2]) minutes = Number(matches[2]);
			    if (matches[3]) seconds = Number(matches[3]);
			    totalseconds = hours * 3600  + minutes * 60 + seconds;
			  }

			  return (totalseconds);
			}

			function formatYTcaption(d){
				if (d.length > 150){
					d = d.substr(0,147) + '...';
				}
				return d;
			}

			function formatYTduration(s){
				s = convertToSeconds(s);
				var seconds = 0;
				var minutes = 0;
				var duration = 'length ';
				minutes = Math.floor(s / 60);
				seconds = s % 60;
				if (seconds < 10){
					seconds = '0' + seconds;
				}
				duration = duration + minutes + ':' + seconds;

				return duration;
			}




