function setWrapperClass() {
	var url = new String(window.location.pathname),
		dirs = url.split('/'),
		classes = ' home';

	if (dirs) {
		classes = '';
		for (i = 0; i < dirs.length - 2; i++) { // minus 2 ignores pages folder and page name
			var dir = dirs[i].split('.'); // removes file extentions
			
			if (dir[0]) {
				classes += ' ';
				classes += dir[0].toLowerCase();
			}
		}
	}
	// check if wrapper has an existing class
	var wrapper = document.getElementById("tps-wrap");
	if (!wrapper.className) {
		wrapper.className = classes;
	} else {
		var newClassName = wrapper.className;
		newClassName += classes;
		wrapper.className = newClassName;
	}
}
setWrapperClass();

$('#s4-workspace').scroll(function() { /* using s4-workspace insead of window because SP still breaks normal scrolling */
    if ($('#s4-workspace').scrollTop() > 1){  
        $('nav').addClass("shrink");
    }
    else{
        $('nav').removeClass("shrink");
    }
});
