var imgArray = new Array(0);

function buildMoArray(fname)
{
	var tmpImg = new Image();
	tmpImg.src = '/images/layout/design21/' + fname + '.jpg';
	imgArray.length++;
	imgArray[imgArray.length-1] = tmpImg;
}

