    $(document).konami({
        result: function () {
            var x;
            var canvas = document.createElement('canvas');
            canvas.width = window.innerWidth;
            canvas.height = 400;
            $(canvas).css({ "width": "100vw", "position": "fixed", "z-index": "5000000", "pointer-events": "none", "left": "0", "bottom": "-400px" });
            $(canvas).appendTo("body");
            function draw(eq) {
                if (canvas.getContext) {
                    var ctx = canvas.getContext('2d');
                    //ctx.clearRect(0, 0, canvas.width, canvas.height);
                    ctx.fillStyle = "#2267FFAA";
                    // Cubic curves example
                    ctx.beginPath();
                    x = 0;
                    ctx.moveTo(0, 100);
                    var y = eval(eq);
                    ctx.lineTo(0, eval(eq));
                    var w = canvas.width / 10;
                    x = 20;
                    ctx.bezierCurveTo(w, y, w, eval(eq), w * 2, eval(eq));
                    y = eval(eq);
                    x = 40;
                    ctx.bezierCurveTo(w * 3, y, w * 3, eval(eq), w * 4, eval(eq));
                    y = eval(eq);
                    x = 60;
                    ctx.bezierCurveTo(w * 5, y, w * 5, eval(eq), w * 6, eval(eq));
                    y = eval(eq);
                    x = 80;
                    ctx.bezierCurveTo(w * 7, y, w * 7, eval(eq), w * 8, eval(eq));
                    y = eval(eq);
                    x = 100;
                    ctx.bezierCurveTo(w * 9, y, w * 9, eval(eq), w * 10, eval(eq));
                    ctx.lineTo(w * 10, canvas.height);
                    ctx.lineTo(0, canvas.height);
                    ctx.fill();
                }
            }
            var i = 0;
            setInterval(function () {
                i += 0.01;
                if (canvas.getContext) {
                    var ctx = canvas.getContext('2d');
                    ctx.clearRect(0, 0, canvas.width, canvas.height);
                }
                draw("(Math.sin((x * Math.PI / 180) * 30 + i) * 20) + 50");
                $(canvas).css("bottom", Math.min((i * 2) - 371, 0).toString() + "px");
            }, 1);
        }
    });

