$(".hpSchools").hover(function () {
    var nht = $(".hpSchoolsTop").outerHeight();
    var nhb = $(".hpSchoolsBottom").outerHeight();
    $(".hpSchools").css("height", (nht + nhb).toString() + "px");
},
function () {
    $(".hpSchools").css("height", "");
});
$(window).resize(function () {
    if ($(".hpSchools").css("height") != "57px") {
        var nht = $(".hpSchoolsTop").outerHeight();
        var nhb = $(".hpSchoolsBottom").outerHeight();
        $(".hpSchools").css("height", (nht + nhb).toString() + "px");
    }
});
