    $(document).ready(function() {
        try {

            // switch the password field to text, then back to password to see if it supports
            // changing the field type (IE9+, and all other browsers do). then switch it back.
            var passwordField = document.getElementById('pwd');
            passwordField.type = 'text';
            passwordField.type = 'password';
            
            // if it does support changing the field type then add the event handler and make
            // the button visible. if the browser doesn't support it, then this is bypassed
            // and code execution continues in the catch() section below
            var togglePasswordFieldButton = document.getElementById('togglePasswordButton');
            togglePasswordFieldButton.style.display = 'table-cell';
            
        }
        catch(err) {

        }
    });

    cs2.tools.login={
        alert:function(cls,text){
            var tpl = '
{text}';
            tpl = tpl.replace('{cls}', cls);
            tpl = tpl.replace('{text}', text);
            $('#userLoginAlertHolder').html(tpl);
        },
        start:function(){
            $('#userLogin').modal();
        },
        contactmodal:function(){
            $('#userLoginUnlock').modal();
        },
        forgotpw:function(){
            $('#forgotUsername').val($('#username').val())
            $('#userLoginPWreset').modal();
        },
        logout:function(){
            $.ajax({url:"/action/logout.cfm",complete:function(){
                window.location.reload(true);
            }});
            return false;
        },
        onsubmit:function(type){
            if(type == 'unlock'){
                var arr = $('#userLoginUnlock form').serializeArray();
                var username = $('#username').val();
                var data = {
                    action:"unlockAccount",
                    user:username,
                    schoolid:currentSchool.id
                };
                for(var idx in arr){
                    var name=arr[idx].name;
                    var value=arr[idx].value;
                    data[name] = value;
                }
                delete data.remove;
                $.ajax({
                    url:"",
                    method:"POST",
                    data:data,
                    dataType:"json",
                    complete:function(e){
                        var js = e.responseJSON;
                        $('#userLoginUnlock').modal("toggle");
                        cs2.tools.captcha.reset("useronlock");
                        if(js.success){
                            cs2.tools.login.alert("success"," Account Unlock Your request has been sent to the webmaster email associated with "+currentSchool.Name+".")
                        }else{
                            var reason = js.reason;
                            cs2.tools.login.alert("warning","Error  {reason}".replace("{reason}",reason));
                        }
                    }
                });
            }else if(type =='forgot'){
                var arr = $('#userLoginPWreset form').serializeArray();
                var username = $('#username').val();
                var data = {
                    action:"forgotpw",
                    user:username,
                    schoolid:currentSchool.id
                };
                for(var idx in arr){
                    var name=arr[idx].name;
                    var value=arr[idx].value;
                    data[name] = value;
                }
                delete data.remove;
                $.ajax({
                    url:"",
                    method:"POST",
                    data:data,
                    dataType:"json",
                    complete:function(e){
                        var js = e.responseJSON;
                        $('#userLoginPWreset').modal("toggle");
                        cs2.tools.captcha.reset("forgotpwcaptcha");
                        if(js.success){
                            cs2.tools.login.alert("success","Success You have been sent an email with a link to reset your password.")
                        }else{
                            var reason = js.reason;
                            cs2.tools.login.alert("warning","Error  {reason}".replace("{reason}",reason));
                        }
                    }
                });
            }
            return false;
        },
        login:function(){
            cs2.tools.login.alert('info','');
            var data = {action:'login',csrf:"40CE48B2385CA5A7C770509841331BF182CD2395", u:$('#username').val(),p:$('#pwd').val(),ldap:$('#ldaplogin').length==1,domain:$('#domain').val()};
            $('#pwd').val('');
            $.ajax({
                url:'',
                method:'POST',
                data:data,
                dataType:'json',
                error:function(e,v){
                    console.log("Error!  Please report this to support@iescentral.com if you see this.", e,v);
                },
                success:function(e,v){
                    if(e.success){
                        window.location.reload();
                    }else{
                        if(e.reason === 'attempts'){
                            cs2.tools.login.alert('danger','Account Disabled Repeat failed login attempts have disabled this account for security purposes.
Please contact your Webmaster in order to unlock your account.');
                        }
                        else
                            cs2.tools.login.alert('danger','Login Failed Please ensure your credentials are correct.  Repeat login failures will result in your account being disabled.');
                    }
                }});
            return false;
        },
        togglePasswordField:function(){
            var passwordField = document.getElementById('pwd');
            var value = $('#pwd').val();
            if($('#togglePasswordButton > span').hasClass('fa-eye')) {
                $('#togglePasswordButton > span').removeClass('fa-eye');
                $('#togglePasswordButton > span').addClass('fa-eye-slash');
            }
            else {
                $('#togglePasswordButton > span').addClass('fa-eye');
                $('#togglePasswordButton > span').removeClass('fa-eye-slash');
            }

            if(passwordField.type == 'password') {
                passwordField.type = 'text';
            }
            else {
                passwordField.type = 'password';
            }
            
            passwordField.value = value;

        }
    };

cs2 = (typeof cs2 === "undefined") ? {} : cs2;
