cs2.tools.widget = (typeof cs2.tools.widget === "undefined") ? {} : cs2.tools.widget;
cs2.tools.widget.render = function(method, args, isSaving) {
    var fn = window[method];
    if(typeof fn === "function") {
        fn.apply(null, args);
    }
};

