cs2.templatemods.startLightSlider = function(module, widget, autoscroll, slideDelay, slideSpeed, responsive, vertical, verticalHieght, enableDrag, mouseDrag, touchDrag, useCSS) { //instantiates the links module lightslider on drop (stop in modals.cfm) and also on load
    if(typeof(autoscroll) == 'undefined') autoscroll = false;
    if(typeof(vertical) == 'undefined') vertical = false;
    if(typeof(verticalHeight) == 'undefined') verticalHeight = 1200;
    if(typeof(enableDrag) == 'undefined') enableDrag = true;
    if(typeof(mouseDrag) == 'undefined') mouseDrag = true;
    if(typeof(touchDrag) == 'undefined') touchDrag = true;
    if(typeof(slideDelay) == 'undefined') slideDelay = 4;
    if(typeof(slideSpeed) == 'undefined') slideSpeed = 0.6;
    if(typeof(useCSS) == 'undefined') useCSS = true;
    if(typeof(responsive) == 'undefined') responsive = [
        {
            breakpoint:800,
            settings: {
                item:3,
                slideMove:1,
                slideMargin:6,
            }
        },
        {
            breakpoint:480,
            settings: {
                item:2,
                slideMove:1
            }
        },
        {
            breakpoint:426,
            settings: {
                item:1,
                slideMove:1
            }
        }
    ];
    module = $(module);
    var item = module.data("item");
    var slideMove = module.data("slidemove");
    var temp = {};
    temp.module = module;
    console.log("enable drag: ", enableDrag);
    console.log("mouse drag: ", mouseDrag);
    console.log("touch drag: ", touchDrag);
    console.log("responsive: ", responsive);
    console.log(' ');
    var ls = $(module).lightSlider({
        item:item,
        loop:false,
        slideMove:slideMove,
        easing: 'cubic-bezier(0.25, 0, 0.25, 1)',
        speed: slideSpeed*1000,
        vertical: vertical,
        verticalHeight: verticalHeight,
        enableDrag: enableDrag,
        mouseDrag: mouseDrag,
        touchDrag: touchDrag,
        responsive : responsive,
        onSliderLoad: function() {
            $(temp.module).removeClass('cS-hidden');
        },
        auto: autoscroll,
        pauseOnHover: true,
        keyPress: true,
        pause: slideDelay * 1000,
        useCSS: useCSS
    });
    cs2.templatemods.lightSliderInstances = (typeof cs2.templatemods.lightSliderInstances == "undefined") ? [] : cs2.templatemods.lightSliderInstances;
    cs2.templatemods.lightSliderInstances.push(ls);
};

cs2 = (typeof cs2 == "undefined") ? {} : cs2;
cs2.contentBuilder = (typeof cs2.contentBuilder == "undefined") ? {} : cs2.contentBuilder;
cs2.contentBuilder.instances = (typeof cs2.contentBuilder.instances == "undefined") ? [] : cs2.contentBuilder.instances;
( function( $, window, document, undefined ) {
    "use strict";

    // undefined is used here as the undefined global variable in ECMAScript 3 is
    // mutable (ie. it can be changed by someone else). undefined isn't really being
    // passed in so we can ensure the value of it is truly undefined. In ES5, undefined
    // can no longer be modified.

    // window and document are passed through as local variables rather than global
    // as this (slightly) quickens the resolution process and can be more efficiently
    // minified (especially when both are regularly referenced in your plugin).

    // Create the defaults once
    var pluginName = "contentBuilder",
        defaults = {
            propertyName: "value"
        };

    // The actual plugin constructor
    function ContentBuilder ( element, options ) {
        this.element = $(element);
        this.activeRow = null;
        this.activeColumn = null;
        this.widgetData = [];

        // jQuery has an extend method which merges the contents of two or
        // more objects, storing the result in the first object. The first object
        // is generally empty as we don't want to alter the default options for
        // future instances of the plugin
        this.settings = $.extend( {}, defaults, options );
        this._defaults = defaults;
        this._name = pluginName;

        // console.log(this.settings);

        this.setWidgets = function(serialData) {
            var newWidgets = JSON.parse(serialData);
            Array.prototype.push.apply(this.widgetData, newWidgets);
        };
        this.renderWidgets = function() {
            for(var i = this.widgetData.length - 1; i >= 0; i--) {
                var ppData = this.widgetData.splice(i, 1)[0];
                var fn = window[ppData.method];
                var args = ppData.args;
                if(typeof fn === "function") {
                    fn.apply(null, args);
                }
            }
        };

        this.setActiveRow = function(jQueryRow) {
            this.activeRow = jQueryRow;
        };

        this.setActiveColumn = function(jQueryColumn) {
            this.activeColumn = jQueryColumn;
        };

        this.setRowFormat = function(id) {
            var allColumns = this.activeRow.find(".column.structure");
            var orphanedModules = [];

            var structures = {
                1:[12],
                2:[6,6],
                3:[4,4,4],
                4:[3,3,3,3],
                5:[10,2,2],
                6:[5,7],
                7:[7,5],
                8:[4,8],
                9:[8,4],
                10:[3,9],
                11:[9,3],
                12:[3,6,3],
                13:[2,8,2],
                14:[3,3,8],
                15:[8,3,3],
                16:[2,2,8]
            };
            var structure = structures[id];//[6, 6];

            if(allColumns.length > structure.length) {
                $.each(allColumns, function(ci, column) {
                    column = $(column);
                    //remove bootstrap class name from the column
                    for(var i = 1; i <= 12; i++) {
                        var className = "col-xs-" + i;
                        column.removeClass(className);
                    }

                    var colSize = structure[ci];
                    // console.log(ci);
                    if(typeof structure[ci] !== "undefined") {
                        column.addClass("col-xs-" + colSize);
                        column.data("size", colSize);
                    }
                    else {
                        var colModules = column.find("div.parentDrag");
                        $.each(colModules, function(i, mod) {
                            orphanedModules.push(mod);
                        });
                        column.fadeOut();
                        column.remove();
                    }
                });
            }
            else {
                var _this = this;
                $.each(structure, function(ci, colSize) {
                    var column = $(allColumns[ci]);
                    if(column.length == 0) {
                        $(_this.activeRow.find(".row.structure")).append(_this.getColumnHTML(colSize));
                    }
                    else {
                        for(var i = 1; i <= 12; i++) {
                            var className = "col-xs-" + i;
                            column.removeClass(className);
                        }
                        column.addClass("col-xs-" + colSize);
                        column.data("size", colSize);
                    }
                });
                this.initDragNDrop();
            }

            //render orphaned modules, distributed across new existing columns
            // console.log(orphanedModules);
            $.each(orphanedModules, function(i, mod) {
                var targetCol = allColumns[(i % structure.length)];
                targetCol.append(mod);
            });
        }

        this.addRowModule = function() {
            var portalBody = {
                maID: 130,
                wID: 1,
                gcdID: currentPageGCDID,
                s:currentSchool.id,
                ts:new Date().getTime(),
            };
            portalBody["portletStyle"] = "edgeless";
            portalBody["contentarea"] = true;
            portalBody["canAdmin"] = canAdmin;
            portalBody["gcdid"] = currentPageGCDID;
            $.ajax({
                url: '/ajax/responsive/Portal/createPortal.cfm',
                method: 'GET',
                data: portalBody,
                dataType:'json',
                success: function(response,v){
                    // console.log(response);
                    if(response.success){
                        var portletShell = '
'
                        $(cs2.contentBuilder.active.activeRow.closest(".parentDrag")).after(portletShell);
                        cs2.tools.widget.render(response.func.method, response.func.args);
                    }else
                        widgetAlert('There was an error saving the widget.  Please contact your system administrator.','danger',false,false);
                },
                error:function(e,v){
                    // console.log(e,v);
                    widgetAlert('There was an error saving the widget.  Please contact your system administrator.');
                }
            });
        }

        this.getColumnHTML = function(colSize) {
            return '
\

\
                    \
                \
            ';
        }

        this.addRow = function() {
            this.addRowModule();
        }

        this.deleteActiveRow = function() {
            var allRows = this.getAllRows();
            if(allRows.length != 1 && typeof this.activeRow !== "undefined") {
                var orphanedRowModules = this.activeRow.find("div.parentDrag");
                this.activeRow.remove();
                var _this = this;
                var firstRow = _this.getAllRows()[0];
                var firstCol = $(firstRow).find(".column");
                $.each(orphanedRowModules, function(i, mod) {
                    var mod = $(mod);
                    $(firstCol).append(mod);
                });
            }
            else {
                alert("You Must Have At Least One Row!");
            }
        }

        this.getAllRows = function() {
            return this.element.find("div.row.structure");
        }

        this.initDragNDrop = function() {
            if(window.innerWidth >= 768) {
                this.element.find(".contentDrop").sortable({
                handle:".panel-header:not(span.pull-right)",
                items:'.parentDrag',
                connectWith: ".contentareamodule .contentDrop",
                cancel: ".panel-header > span.pull-right",
                placeholder:'placeholder',
                tolerance:"pointer",
                cursorAt: { top:0, left: 0 },
                start: function(event, ui) {
                    // console.log("start",event.target);
                // Resize elements
                $(this).sortable('refreshPositions');
                var test = '.'+ui.item["0"].classList["0"]+' .pageData';
                $('.'+ui.item["0"].classList["0"]+' .pageData').hide();
                $('.'+ui.item["0"].classList["0"]+' .swiper-container-horizontal').hide();
                $('.'+ui.item["0"].classList["0"]+' .alertHolder').hide();
                $('.'+ui.item["0"].classList["0"]+' .calHolder').hide();
                },
                sort: function(e,ui) {
                    //console.log("sort",e);
                    //$('.'+ui.item["0"].classList["0"]+' .pageData').hide();
                },
                stop: function( event, ui ) {
                    // console.log("stop",event);
                    $('.'+ui.item["0"].classList["0"]+' .pageData').show();
                    $('.'+ui.item["0"].classList["0"]+' .swiper-container-horizontal').show();
                    $('.'+ui.item["0"].classList["0"]+' .alertHolder').show();
                    $('.'+ui.item["0"].classList["0"]+' .calHolder').show();
                    //start links code
                    var linksSliderNode = $(ui.item).find('ul.linksTemplateModule');//soo, when you drag/drop links widget (which is generated from the module), the javascript gets the surrounding box size and renders the slideshow. Hence, if a widget is in a big box, it get's rendered as a larger slider, then when you move to a smaller box, it stays large (rendered once), so we re-render it here by checking if the dropped widget is the links (b/c the links has this special class .linksTemplateModule in the view.)
                    if(typeof linksSliderNode != "undefined") {
                        $.each(cs2.templatemods.lightSliderInstances, function(i, v) {
                            if('##'+linksSliderNode.attr('id') == v.selector) {
                            v.refresh();
                            }
                        });
                    }
                    //end links widget code
                }
                });
            }

            this.element.find(".main-content-area > .row-wrapper").sortable({
                handle: ".panel-header",
                cancel: ".panel-header > span.pull-right"
            });
        }

        this.init();
    }

    // Avoid Plugin.prototype conflicts
    $.extend( ContentBuilder.prototype, {
        init: function() {

            // Place initialization logic here
            // You already have access to the DOM element and
            // the options via the instance, e.g. this.element
            // and this.settings
            // you can add more functions like the one below and
            // call them like the example below
            this.initDragNDrop();
            this.initEvents();
        },
        initEvents: function() {
            this.element.on("click", ".js-add-mod", function(e) {
                windowAddWidget2({wID: currentPortalID, gcdID:currentPage.gcdID, mode:'contentArea'});
            });
        },
        getStructure: function() {
            var struct = [];
            $.each(this.element.find(".row-wrapper").children(".parentDrag"), function(i, rowModule) {//going though each row module as rowModule.
                rowModule = $(rowModule);
                var row = {};
                row["ppid"] = $(rowModule).data("ppid");
                // var rowData = rowModule.find(".contentrow");
                // row.columns = [];

                // $.each(rowModule.find(".column.structure"), function(i, column) {
                //     var colData = {};
                //     colData.size = $(column).data('size');
                //     colData.modules = [];
                //     $.each($(column).find(".parentDrag"), function(i, port) {
                //         var portlet = {};
                //         portlet.ppid = $(port).data("ppid");
                //         colData.modules.push(portlet);
                //     });
                //     row.columns.push(colData);
                // });
                struct.push(row);
            });
            return struct;
        }
    } );

    // A really lightweight plugin wrapper around the constructor,
    // preventing against multiple instantiations
    $.fn[ pluginName ] = function( options ) {
        return this.each( function() {
            if ( !$.data( this, pluginName ) ) {
                var pluginData = new ContentBuilder( this, options );
                $.data( this, 
                    pluginName, pluginData );
                cs2.contentBuilder.instances.push(pluginData);
            }
        } );
    };

    } )( jQuery, window, document );

    // document.addEventListener('caDidAdd', function (e) {
    //     var portletid = e.detail;
    // }, false);

    document.addEventListener('click', function(event) {
        if (event.target.classList.contains("js-row-selector") || event.target.classList.contains("js-add-row") || event.target.classList.contains("js-delete-row") || event.target.classList.contains("add-wrapper")) {
            var mainArea = $($(event.target).closest(".contentareamodule"));
            var contentBuilder = $(mainArea).data("contentBuilder");
            if(typeof contentBuilder === "undefined") {
                mainArea.contentBuilder();
                contentBuilder = mainArea.data("contentBuilder");
            }
            cs2.contentBuilder.active = contentBuilder; //set active content builder
            contentBuilder.setActiveRow($(event.target).closest(".parentDrag")); //todo vm
        }
    }, true);

    document.addEventListener('click', function(event) {
        if (event.target.classList.contains("add-wrapper") || event.target.classList.contains("js-add-mod")) {
            var mainArea = $($(event.target).closest(".contentareamodule"));
            var contentBuilder = $(mainArea).data("contentBuilder");
            // if(typeof contentBuilder === "undefined") {
            //     mainArea.contentBuilder();
            //     contentBuilder = mainArea.data("contentBuilder");
            // }
            cs2.contentBuilder.active = contentBuilder; //set active content builder
            contentBuilder.setActiveColumn($(event.target).closest(".column.structure"));
        }
    }, true);

    $(document).on('click', '.js-row-selector', function(event) {
        var cursorX = event.pageX;
        var cursorY = event.pageY;
        $("#row-selector").css('left', cursorX);
        $("#row-selector").css('top', cursorY + 20);
        $("#row-selector").fadeToggle();
        event.stopPropagation();
    });

    $(document).on('click', '.js-add-row', function(e) {
        cs2.contentBuilder.active.addRow();
        e.stopPropagation();
        return false;
    });

    $(document).on('click', '.js-delete-row', function(e) {
        cs2.contentBuilder.active.deleteActiveRow();
        e.stopPropagation();
    });

    $(document).on('click', '.js-select-layout', function(e) {
        cs2.contentBuilder.active.setRowFormat($(e.currentTarget).data("id"));
        $("#row-selector").hide();
        e.stopPropagation();
    });

            // Plugin @RokoCB :: Return the visible amount of px
            // of any element currently in viewport.
            // stackoverflow.com/questions/24768795/
            (function($, win) {
            $.fn.inViewport = function(cb) {
                return this.each(function(i,el){
                function visPx(){
                    var H = $(this).height(),
                        r = el.getBoundingClientRect(), t=r.top, b=r.bottom;
                    return cb.call(el, Math.max(0, t>0? H-t : (b<H?b:H)));  
                } visPx();
                $(win).on("resize scroll", visPx);
                });
            };
            }(jQuery, window));

            if(typeof cs2 == 'undefined') {cs2={};}
            if(typeof cs2.modules == 'undefined') {cs2.modules={};}
            if(typeof cs2.modules.iconPicker == 'undefined') {cs2.modules.iconPicker = {};}

            cs2.modules.iconPicker.open = function(iconArea) {
                $('#csModuleIconPicker .accIcon.selected').removeClass('selected');
                jQuery('#csModuleIconPicker').modal('show');  
                var iconArea = jQuery(iconArea);
                cs2.modules.iconPicker.iconField = iconArea.data("preview-target");
                cs2.modules.iconPicker.passedIcon = jQuery(cs2.modules.iconPicker.iconField).val();
                cs2.modules.iconPicker.selectedIcon = "";
            }

            cs2.modules.iconPicker.save = function() {
                jQuery(cs2.modules.iconPicker.iconField).val(cs2.modules.iconPicker.selectedIcon).trigger('change');
                jQuery('#csModuleIconPicker').modal('hide');
            }

            jQuery(document).ready(function() {
                $('#csModuleIconPicker .accSearch').keyup(function(){
                    var sender = $(this);
                    var search = sender.val();
                    $('.accIcon.hide').removeClass('hide');
                    var found = [];
                    $('#csModuleIconPicker .accIcon').each(function(){
                        var target = $(this);
                        var icon = target.data('icon');
                        if(typeof icon !== 'undefined'){
                            icon = icon.substr(3);
                            if(icon.toLowerCase().includes(search.toLowerCase())){
                                found.push(icon);
                            }
                        }
                    });
                    var content = "
";
                    var rowWidth = 10;
                    if(screen.width < 768)
                        rowWidth = 6;
                    for(var x = 0;x < found.length;x++){
                        var icon = found[x];
                        var iconTitle = icon.substring(3);
                        if(x%rowWidth == 0)
                            content += "
";
                        content += "
" +
                        ''
                        + "";
                    }
                    content += "";
                    $('.container-fluid.foundIcons').html(content);
                    if($('.container-fluid.foundIcons').css('display') == 'none')
                        $('.container-fluid.foundIcons').toggle();
                });
                jQuery("#csModuleIconPicker .accIcon").click(function(sender) {
                    $('#csModuleIconPicker .accIcon.selected').removeClass('selected');
                    $(this).addClass('selected');
                    var icon = jQuery(this).find('span').data('icon-name');
                    cs2.modules.iconPicker.selectedIcon = icon;
                });
            });
            
            jQuery(document).on('change', '.fontawesomeHiddenInput', function() {
                // setting the new previous value
                var thisValue = $(this).val();
                jQuery(this).parent().find("span.fa").removeClass().addClass("fa").addClass(thisValue);
            });

