var moduleList = {
  userLogin: { name: 'User Login', wait: false, func: 'userLogin', reported: false, loaded: false },
  userLoginIES: { name: 'IES Special Login', wait: false, func: 'userLoginAs', reported: false, loaded: false },
  userLoginAs: { name: 'Login As...', wait: false, func: 'userLoginAs', reported: false, loaded: false },
  userSwitch: { name: 'Switch Users', wait: false, func: 'userSwitch', reported: false, loaded: false },
