function checkModuleLoaded(moduleName){
  if (!moduleList[moduleName])
    return false;
  if (moduleList[moduleName].requires && moduleList[moduleName].requires.length > 0){
    var mods = moduleList[moduleName].requires;
    var modLen = mods.length;
    loaded = moduleList[moduleName].reported;
    var nn=0;
    for(var n=0; n 0){
        var curLoad = moduleList[moduleName].loadedCt || 0;
        var modLen = moduleList[moduleName].requires.length;
        Ext.onReady(function(){ 
          Ext.Msg.alert('Error Loading '+moduleList[moduleName].name+' ['+curLoad+'/'+modLen+']', moduleList[moduleName].name+' failed to load due to a connection problem. Please try again in a few seconds.');
        });
      }else{
        Ext.onReady(function(){ 
          Ext.Msg.alert('Error Loading '+moduleList[moduleName].name, moduleList[moduleName].name+' failed to load due to a connection problem. Please try again in a few seconds.');
        });
      }
      return false;
    }
    if (!moduleList[moduleName].wait){
      Ext.onReady(function(){ 
        Ext.Msg.wait('Please wait...', 'Loading '+moduleList[moduleName].name);
      });
      moduleList[moduleName].wait=true;
    }
    if (moduleList[moduleName].requires && moduleList[moduleName].requires.length > 0){
      var curLoad = moduleList[moduleName].loadedCt || 0;
      var modLen = moduleList[moduleName].requires.length;
      Ext.onReady(function(){ 
        Ext.Msg.updateText('Loaded ['+curLoad+' of '+modLen+'] helpers. Please wait...');
      });
    }
    moduleList[moduleName].timer = setTimeout(function(){ launchModule(moduleName, aargs); }, 100);
  }
}
var normalLoadTimePassed = false;
var maxLoadTimePassed = false;
setTimeout(function(){ normalLoadTimePassed=true }, 10000);
setTimeout(function(){ maxLoadTimePassed=true }, 30000);
function reportingIn(moduleName){

  if(!moduleList[moduleName])
    moduleList[moduleName] = {slave: true};

  var thisModule = moduleList[moduleName];

  thisModule.reported = true;

    var found = false;
    var foundReq = "";
    for(var mod in moduleList){
      checkModuleLoaded(mod);
      var eMod = moduleList[mod];
      if (eMod.requires && eMod.requires.length > 0){
        var mods = eMod.requires;
        var modLen = mods.length;
        for(var n=0; n<modLen; n++){
          if (mods[n] == moduleName){
            foundReq = mods[n];
            found = true;
            break;
          }
        }
      }
    }

  Ext.onReady(function(){
    if (found && foundReq && moduleList[foundReq] && moduleList[foundReq].loaded){
      var nodes = Ext.query('.loadingIcon_'+foundReq);
      for (var c=0;c<nodes.length;c++)
        Ext.fly(nodes[c]).removeClass('loadingIcon');
    }
    for(var mod in moduleList){
      if (moduleList[mod].loaded == true){
        var nodes = Ext.query('.loadingIcon_'+mod);
        for (var c=0;c<nodes.length;c++)
          Ext.fly(nodes[c]).removeClass('loadingIcon');
      }
    }
    if (thisModule.loaded){
      var nodes = Ext.query('.loadingIcon_'+moduleName);
      for (var c=0;c<nodes.length;c++)
        Ext.fly(nodes[c]).removeClass('loadingIcon');
    }
  });

}

