function waitForJquery(callback,speed,tries){
    var tries=typeof tries=='undefined'?0:tries;
    var speed=typeof speed=='undefined'?500:speed;
    if(tries > 20){
        console.log("Giving Up");
        return;
    }
    if(typeof($) != 'undefined')
        callback();
    else
        setTimeout(function(){waitForJquery(callback,speed,++tries);},speed);
}

