$(document).ready(function() {
    jQuery.post('/ada/urladastatus.cfm',{url:window.location.href.split("?")[0].split("#")[0]},function(result) {
      result = JSON.parse(result);
      if(result.status == "fail") {
        jQuery('.adaCheckIcon').addClass('failIcon').removeClass('adaCheckIcon');
      }
      else if(result.status == "pass") {
        jQuery('.adaCheckIcon').addClass('passIcon').removeClass('adaCheckIcon');
      }
      else {
        jQuery('.adaCheckIcon').addClass('checkIcon').removeClass('adaCheckIcon');
      }
    });

  $("ul.dropdown-menu > li:last-child").on("keydown", function(e) {
    var keyCode = e.keyCode || e.which;
    if (keyCode == 9) { 
      $(this).closest(".dropdown").toggleClass("open");
      $(this).closest("div.topPagerLoggedInFunctions").toggleClass("open"); //login bar
    }
  });

  $('#adaModal').on('shown.bs.modal', function() {
    $('#adaModal .close').trigger('focus');
  });

  //this code fixes the annoying thing that happens on the templatemods (links especially) when a modal is to tall (scroll needed) and you open another modal (select image) that close removes the modal-open class from body and you cant scroll so this fixes that.
  $('.modal').on("hidden.bs.modal", function (e) { 
      if ($('.modal:visible').length) { 
          $('body').addClass('modal-open');
      }
  });

});

