function toggleHoliday(){
    var data = {submit:1,type:'holiday'};
    $.ajax({
        url:'',
        method:"POST",
        dataType:'JSON',
        data:data,
        complete:function(e,v){
            var js = e.responseJSON;
            if(js.success){
                if(js.enabled){
                    if(typeof holiday != 'undefined'){
                        holiday.start();
                    }else{
                        var script = document.createElement('script');
                        script.src = '/common/snow.js';
                        document.head.appendChild(script); 
                    }
                }
                else
                    holiday.stop();
            }
        }
    });
}

