  var siteNotice = false;
  function dropSiteNotice(){
    new Ajax.Request('/supressMessages.cfm?supress=false');
    Effect.BlindDown('siteNoticeBoxHolder', { duration: 0.5 })
    $('pagerBarToolAlertButton').addClassName('buttonOn');
  }
  function liftSiteNotice(){
    new Ajax.Request('/supressMessages.cfm?supress=true');
    Effect.BlindUp('siteNoticeBoxHolder', { duration: 0.25 })
    $('pagerBarToolAlertButton').removeClassName('buttonOn');
  }
  function toggleSiteNotice(){ 

    if ($('pagerBarToolAlertButton').hasClassName('buttonOn')){
      liftSiteNotice();
    }else{
      dropSiteNotice();
    }

  }

  var e=new Date;
  function SystemAlert(){
    $.get("/ajax/notifications/getAlertMessage.cfm?json=1&school_id="+currentSchool.id+"&gcdid=0&now="+e.format("Y-m-d H:i:s"),function(data){
      parsed = $.parseJSON(data);
      $('#siteAlertModal .modal-title').html(parsed.DATA["0"]["0"]);
      $('#siteAlertModal .modal-body').html(parsed.DATA["0"]["1"]);
      $('#siteAlertModal').modal('show');
    });
  }
  Ext.Ajax.request({
    url:"/ajax/notifications/getUniqueCookie.cfm?school_id="+currentSchool.id+"&gcdid=0&now="+e.format("Y-m-d H:i:s"),
    success:function(e){
      var t=Ext.util.JSON.decode(e.responseText);
      if(t.success&&t.uniqueKey){
        var a=Ext.util.Cookies.get("systemAlert");
        if(cs2.page.homepage && typeof sticky == 'undefined' && t.sticky == 1 || (typeof currentPageGCDID !== "undefined" && currentPageGCDID > 0 && t.sticky == 1)){
            Ext.get("SystemAlertBtn").setStyle('display','inline-block');
            SystemAlert();
            Ext.util.Cookies.set("systemAlert",t.uniqueKey);
        }
        else{
            Ext.get("SystemAlertBtn").setStyle('display','inline-block'), a&&a==t.uniqueKey||(""!=t.uniqueKey&&SystemAlert(), Ext.util.Cookies.set("systemAlert",t.uniqueKey)
            )}
        }
      }}
      );

