          var cookieRecall = Ext.util.Cookies.get("CurrentNavID");

          if(!cookieNavArrayStruct)
            var cookieNavArrayStruct = {};
          var base64Url = Base64.encode(document.URL);


          if(cookieNavArrayStruct.hasOwnProperty(base64Url)) 
            if(cookieRecall != cookieNavArrayStruct[base64Url] || (cookieRecall == "" && cookieNavArrayStruct[base64Url] != ""))
              cookieRecall = cookieNavArrayStruct[base64Url];

          if(cookieRecall && cookieRecall != "" && currentPageGCDID == 0){
              var CurrentNavElement = Ext.get(cookieRecall);
              if(CurrentNavElement){
                Ext.util.Cookies.set("CurrentNavID","");
                var tpl = new Ext.Template(
                      '
',
                        '
',
                          '
{headerTitle}',
                          '',
                          '{children}',
                          '',
                        '',
                      '',{
                        compiled: true     // compile immediately
                    }
                );
                if(CurrentNavElement.next())
                    tpl.insertFirst('navSectionHolder', {itemID:CurrentNavElement.id,headerTitle:CurrentNavElement.dom.innerHTML,children:CurrentNavElement.next().next().dom.innerHTML});  
                else
                    tpl.insertFirst('navSectionHolder', {itemID:CurrentNavElement.id,headerTitle:CurrentNavElement.dom.innerHTML,children:CurrentNavElement.parent().parent().next('.horizMenu').dom.innerHTML});  
              }
          }
        });


