    $('.openADAModal').on('click', function(){
      cs2.ada.openADAModal(window.location.href);
    });

    //to open the modal we will first get the url and lookup the url via urladastatus.cfm, which will return the ID of the database row if it exists
    //otherwise it will return 0, this will set 
    cs2.ada.openADAModal = function(url) {
      var cleanUrl = url.split("?")[0].split("#")[0];
      cs2.ada.setSavedADADataURL(cleanUrl);
      jQuery.post('/ada/urladastatus.cfm',{url:cleanUrl},function(result) {
        result = JSON.parse(result);
        generateRaw = {};
        generateRaw.status = result.status;
        generateRaw.issues = result.issueJSON;
        cs2.ada.generateADAIssueUI(generateRaw, 0);
        $("#adaModal").modal({
          autofocus: true
        });
      });
    }
    
    //when the modal closes, lookup the last scanned url and update the dashboard if it exists in it
    $('#adaModal').on('hide.bs.modal', function () {
      if(loggedIn) {
        var adaInfo = {};
        adaInfo.url = cs2.ada.getSavedADADataURL();
        jQuery.post("/ada/urladastatus.cfm", adaInfo, function(response) {
        cs2.ada.updateNavPageByResponse(response);
      });
      }
    });

    cs2.ada.getSavedADADataURL = function() {
      if(typeof cs2.ada.pageData == 'undefined') {
        cs2.ada.setupDefaultPageADAData();
      }
      return cs2.ada.pageData.url;
    }

    cs2.ada.setSavedADADataURL = function(url) {
      if(typeof cs2.ada.pageData == 'undefined') {
        cs2.ada.setupDefaultPageADAData();
      }
      cs2.ada.pageData.url = url;
    }

    cs2.ada.setupDefaultPageADAData = function() {
      var adaData = {};
      adaData.url = window.location.href.split("?")[0].split("#")[0];
      cs2.ada.pageData = adaData;
    }

    cs2.ada.CheckAda = function(testMode){
      //show the loader
      jQuery('.adaIssueBody').slideUp();
      jQuery('.adaCheckerLoader').slideDown();

      var adaInfo = {};
      adaInfo.url = cs2.ada.getSavedADADataURL();
      adaInfo.scan = true;
      if(testMode) {
        adaInfo.testmode = true;
      }
      jQuery.post("/ada/urladastatus.cfm", adaInfo, function(adaInfo) {
        adaInfo = JSON.parse(adaInfo);
        cs2.ada.generateADAIssueUI({"status":adaInfo.status,"issues":adaInfo.issueJSON}, testMode);
        //now you're done updating the UI, now update the database
      }).always(function() {
        jQuery('.adaCheckerLoader').slideUp();
      });
    };

    cs2.ada.ADACheckByURL = function(url, update) {
      return new Promise(function(resolve, reject) {
        jQuery.post("http://ada.corp.iescentral.com:3000/sniffUrl", {'url':url}, function(adaIssues) {
          
        });
      });
    }

    //pass me parsed ada issues array!
    cs2.ada.generateADAIssueUI = function(result, testmode) {
      adaIssues = result.issues;
      //clear out any existing data if opening over and over on the same page without refreshing (ajax appends)
      jQuery('.adaIssuesTabbable ul.nav').empty();
      jQuery('.adaIssuesTabbable div.tab-content').empty();

       if(result.status == "fail") {
        //there are ada issues
        var issueMap = {};
        $.each(adaIssues, function(index, value) {
          if(issueMap[value.id] == undefined) {
            issueMap[value.id] = [];
          }
          issueMap[value.id].push(value);
        });
        jQuery('#adaComplianceIssues').show();
        jQuery('#adaComplianceIssues > span').text(Object.keys(issueMap).length);

        $.each(Object.keys(issueMap), function(i, v) {
          jQuery('.adaIssuesTabbable ul.nav').append('

'+v+'');
          jQuery('.adaIssuesTabbable div.tab-content').append('
');

         
          $.each(issueMap[v], function(issueKey, issueObject) {
            $.each(issueObject.nodes, function(nodeIndex, node) {
              jQuery('#adaIssue-'+i+'').append("HTML: "+node.html.replace(//g,">") +" 
 Selector: 
" + node.target + "
Attempted Render: " +node.html.replace(/script/g,'').replace(/none/g,'')+"");
              if(testmode) {
                jQuery(node.target).css('background-color','rgba(255,255,0,.6)').css('border','2px solid red');
              }
            });
          });
        });
        //ensure the UI is correct - ie if you passed before then failed right now
        var adaTestPass = testmode ? "TESTMODE - FAIL" : "FAIL";
        jQuery('#adaModal .passOrFail').text(adaTestPass).css('background-color','#c33838');
        jQuery('#adaModal .modal-header').css('background-color','#c33838');
        //now show the UI for ada issues.
        jQuery('#adaModal .adaIssueBody').slideDown();
       }
       else if(result.status == "pass") {
         //there are no ada issues
         var adaTestPass = testmode ? "TESTMODE - PASS" : "PASS";
         jQuery('#adaModal .adaIssueBody').slideUp();
         jQuery('#adaModal .passOrFail').text(adaTestPass).css('background-color','#5cb85c');
         jQuery('#adaModal .modal-header').css('background-color','#5cb85c');
       }
       else if(result.status == "ERROR") {
          jQuery('#adaModal .passOrFail').text("Error scanning page, please contact support@iescentral.com").css('background-color','#c33838');
          jQuery('#adaModal .modal-header').css('background-color','#c33838');
       }
       else {
         var adaTestPass = "Not Yet Checked"
         jQuery('#adaModal .adaIssueBody').slideUp();
         jQuery('#adaModal .passOrFail').text(adaTestPass).css('background-color','#D96C00');
         jQuery('#adaModal .modal-header').css('background-color','#D96C00');
       }
      }

      cs2.ada.updateNavPageByResponse = function(response) {
          result = JSON.parse(response);
          var link = jQuery("#adaDashboardNavList ul.list-group li > a[href='"+result.url+"']").closest("li.adalineinstance");
          if(result.status == "pass") {
              jQuery(link).find('span.adastatus').text(result.status.toUpperCase())
                  .removeClass('badge-pass').removeClass('badge-fail').removeClass('badge-unchecked')
                  .addClass("badge-pass");
              jQuery(link).find('span.adaerrorcount').hide();
              jQuery(link).data('url', result.url);
          }
          else if(result.status == "fail") {
              jQuery(link).find('span.adastatus').text(result.status.toUpperCase())
                  .removeClass('badge-pass').removeClass('badge-fail').removeClass('badge-unchecked')
                  .addClass("badge-fail");
              jQuery(link).find('span.adaerrorcount').text('Errors: ' + (result.issueJSON).length).show();
              jQuery(link).data('url', result.url);
          }
          else {
              jQuery(link).find('span.adastatus').text(result.status.toUpperCase())
                  .removeClass('badge-pass').removeClass('badge-fail').removeClass('badge-unchecked')    
                  .addClass("badge-unchecked");
              jQuery(link).find('span.adaerrorcount').hide();
              jQuery(link).data('url', result.url);
          }
      }


