    cs2.tools.toast = function(toastMessage, duration) {
        if(typeof duration === "undefined") { duration = 3000; }
        var x = document.getElementById("snackbar");
        x.className = "show";
        document.getElementById("snackbar").innerHTML = toastMessage;
        setTimeout(function(){ x.className = x.className.replace("show", toastMessage); }, duration);
    }

    cs2 = (typeof cs2 === "undefined") ? {} : cs2;
    cs2.chat = (typeof cs2.chat === "undefined") ? {} : cs2.chat;
    cs2.chat.models = (typeof cs2.chat.models === "undefined") ? {} : cs2.chat.models;

    cs2.chat.models.Chat = function(chatdata) {
        this.id = undefined;
        this.subject = undefined;
        this.schoolid = undefined;
        this.gcdid = undefined;
        this.type = undefined;
        this.public = undefined;
        this.created = undefined;
        this.updated = undefined;
        this.chatMeta = undefined;
        this.users = undefined;
        this.messages = [];
        this.node = undefined;
        this.admin = undefined;
        this.unreadMessages = 0;
        this.lastMessage = undefined;
        this.muted = false;
        this.canChat = false;

        if(chatdata instanceof jQuery) {
            //chatdata is the dom jquery element, need to get data attributes and build
            this.id = chatdata.data("id");
            this.subject = chatdata.data("subject");
            this.schoolid = chatdata.data("schoolid");
            this.gcdid = chatdata.data("gcdid");
            this.type = chatdata.data("type");
            this.public = chatdata.data("public");
            this.created = chatdata.data("created");
            this.updated = chatdata.data("updated");
            this.chatMeta = chatdata.data("chatMeta");
            this.admin = chatdata.data("admin");
            this.users = chatdata.data("users");
            this.muted = chatdata.data("muted");
            this.canChat = chatdata.data("canChat");
            this.unreadMessages = chatdata.data("unreadMessages");
            this.node = chatdata;
            if(!typeof chatdata.data("lastMessage") === "undefined") {
                this.lastMessage = chatdata.data("lastMessage");
            }
        }
        else {
            //chatdata is the server serialized chat class
            this.id = chatdata.id;
            this.subject = chatdata.subject;
            this.schoolid = chatdata.schoolid;
            this.gcdid = chatdata.gcdid;
            this.type = chatdata.type;
            this.public = chatdata.public;
            this.created = chatdata.created;
            this.updated = chatdata.updated;
            this.chatMeta = chatdata.chatMeta;
            this.admin = chatdata.admin;
            this.users = chatdata.users;
            this.lastMessage = chatdata.lastMessage;
            this.unreadMessages = chatdata.unreadMessages;
            this.muted = chatdata.muted;
            this.canChat = chatdata.canChat;
        }

        //set updates the this values. You could do this.name = value and then this.node.data("name") = value but this set function helps with DRY.
        this.set = function(name, value) {
            this[name] = value;
            if(typeof this.node !== "undefined") {
                this.node.data(name, value);
            }
        }

        this.getOtherUsers = function() {
            //otherUsers is now all users except the current user in cs2.chat.currentUser
            var otherUsers = [];
            if(this.users.length > 1) {
                for(var i = this.users.length - 1; i >= 0; i--) {
                    if(this.users[i].chatid != cs2.chat.currentUser.chatid) {
                        otherUsers.push(this.users[i]);
                    }
                }
            }
            return otherUsers;
        }

        this.setMute = function(muted) {
            this.set("muted", muted);
            if(typeof this.node !== "undefined") {
                if(muted) {
                    this.node.find(".badge.badge-muted").fadeIn();
                }
                else { 
                    this.node.find(".badge.badge-muted").fadeOut();
                }
            }
        }

        this.getHTML = function() {
            var fNamesList = this.getOtherUsers().map(function(user){return user.firstName});
            var subject = fNamesList.join(" & ");
            var userAvatar;
            var lastMessage = (typeof this.lastMessage === "undefined" || (typeof this.lastMessage !== "undefined" && this.lastMessage == "")) ? "" : this.lastMessage.content;
            var notificationBadge = this.unreadMessages ? ''+this.unreadMessages+'' : '0';
            var mutedBadge = this.muted ? '' : '';
            if(this.users.length == 0) {
                userAvatar = "/common/images/blogIcon.png";
                subject = this.subject;
            }
            else if(this.users.length > 2 || this.users.length == 1) {
                //this is a group of users chatting
                userAvatar = "/common/images/peopleplaceholder.png";
            }
            else if(this.users[1].type == "subscriber") {
                //we're chatting with a subscriber
                userAvatar = "/common/images/personplaceholder.png";
            }
            else {
                userAvatar = "/images/getProfilePicture.cfm?id="+this.users[1].dbid+"";
            }
            var addUserHTML = (this.type == "group_chat" && (typeof cs2.chat.currentUser != "undefined" && cs2.chat.currentUser.chatid == this.admin)) ? "" : "";
            var chatTemplate = $('
\

\
                    \
                    \

\

'+ subject +' '+ notificationBadge + mutedBadge +'\

'+lastMessage+'\
                        '+addUserHTML+'\
                    \
                \
            ');
            //iterate through all member variables (not functons) to add data to our chatTemplate jQuery object
            for(var key in this) {
                if(this.hasOwnProperty(key) && typeof this[key] !== 'function') {
                    chatTemplate.data(key, this[key]);
                }
            }
            return chatTemplate;
        }

        this.getMessages = function() {
            var body = {
                "action": "getChatMessages",
                "chatdbid": this.id,
                "chatid":cs2.chat.currentUser.chatid
            };
            var _this = this;
            $.ajax({
                method:"POST",
                url:"/ajax/responsive/chats/ajax.cfm",
                data: body,
                dataType: "json"
            }).success(function(messages) {
                $(messages).each(function(i, message) {
                    message = new cs2.chat.models.Message(message);
                    if(!cs2.chat.blockedUserMessage(message)) {
                        _this.messages.push(message);
                    }
                });
                _this.renderMessages();
            });
        }

        this.getOldMessages = function() {
            var body = {
                "action": "getChatMessages",
                "chatdbid": this.id,
                "chatid":cs2.chat.currentUser.chatid,
                "offset":this.messages.length
            };
            var _this = this;
            $.ajax({
                method:"POST",
                url:"/ajax/responsive/chats/ajax.cfm",
                data: body,
                dataType: "json"
            }).success(function(messages) {
                //store a reference to the first message, prepend, then go back to that message to maintain scrolling view.
                var firstMsg = cs2.chat.messageList.find("li:first");
                var curOffset = firstMsg.offset().top - cs2.chat.messageList.scrollTop();
                $(messages).each(function(i, message) {
                    message = new cs2.chat.models.Message(message);
                    if(!cs2.chat.blockedUserMessage(message)) {
                        _this.messages.unshift(message);
                        cs2.chat.messageList.prepend(message.getHTML());
                    }
                    
                });
                cs2.chat.messageList.scrollTop(firstMsg.offset().top-curOffset);
            });
        }

        this.renderMessages = function() {
            var _this = this;
            cs2.chat.messageList.fadeOut(200, function() {
                cs2.chat.messageList.empty();
                $(_this.messages).each(function(i, message) {
                    cs2.chat.messageList.append(message.getHTML());
                });
                cs2.chat.messageList.fadeIn(200, function () {
                    cs2.chat.scrollToBottom(true);
                });
                if(_this.messages.length > 0) {
                    _this.updatePreview(_this.messages[_this.messages.length - 1].content);
                }
            });
        }

        this.setActiveStatus = function(status) {
            if(typeof this.node !== "undefined") {
                if(status) {
                    this.node.addClass("active");
                }
                else {
                    this.node.removeClass("active");
                }
            }
        }

        this.pushMessage = function(message) {
            this.messages.push(message);
            this.updatePreview(message.content);
            //let's keep lastmessage up to date by pushing the new messages we get from the server.
            this.lastMessage = message;
            /* if we are pushing a message to the current chat, we should update the view. Otherwise we should update the notification about unread messages next to the chat -VM */
            if(typeof cs2.chat.currentChat !== "undefined" && this.id == cs2.chat.currentChat.id) {
                var node = message.getHTML();
                node.hide();
                message.node = node;
                /* so the issue becomes, you need to add the node to the dom, then you call scroll down (which detects if user is at bottom of view, if not doesnt scroll unless forced, this is so users won't be interrupted with scrolling if they are reading past chats) so we detect before we add to dom if they are at the bottom, then pass that to the force in the scroll function -VM */
                var _passedData = {};
                _passedData.userAtBottom = cs2.chat.isUserAtBottom();
                cs2.chat.messageList.append(node);
                message.node.fadeIn(200, function () {
                    cs2.chat.scrollToBottom(_passedData.userAtBottom);
                });
                message.setReadStatus(true);
            }
            else {
                this.unreadMessages++;
                this.updateUnreadBadge();
            }
        }

        this.updateUnreadBadge = function() {
            if(typeof this.node !== "undefined") {
                this.node.find(".unreadNotifications").text(this.unreadMessages);
                this.node.data("unreadMessages", this.unreadMessages);
                this.node.find(".unreadNotifications").fadeIn();
            }
        }

        this.clearUnreadBadge = function() {
            if(typeof this.node !== "undefined") {
                this.node.data("unreadMessages", 0);
                this.unreadMessages = 0;
                this.node.find(".unreadNotifications").fadeOut();
            }
        }
        
        this.updatePreview = function(preview) {
            if(typeof this.node !== "undefined") {
                this.node.find(".preview").text(preview);
            }
        }
    }

    cs2.chat.models.Message = function(messagedata) {
        this.id = undefined;
        this.parent_id = undefined;
        this.type = undefined;
        this.content = undefined;
        this.user_id = undefined;
        this.sent = undefined;
        this.deleted = undefined;
        this.meta = undefined;
        this.user = undefined;
        this.node = undefined;

        if(messagedata instanceof jQuery) {
            this.id = messagedata.data("id");
            this.parent_id = messagedata.data("parent_id");
            this.type = messagedata.data("type");
            this.content = messagedata.data("content");
            this.user_id = messagedata.data("user_id");
            this.sent = messagedata.data("sent");
            this.deleted = messagedata.data("deleted");
            this.meta = messagedata.data("meta");
            this.user = messagedata.data("user");
            this.node = messagedata;
        }
        else {
            this.id = messagedata.id;
            this.parent_id = messagedata.parent_id;
            this.type = messagedata.type;
            this.content = messagedata.content;
            this.user_id = messagedata.user_id;
            this.sent = messagedata.sent;
            this.deleted = messagedata.deleted;
            this.meta = messagedata.meta;
            this.user = messagedata.user;
        }

        this.getHTML = function() {
            var sender = (typeof cs2.chat.currentUser !== "undefined" && (this.user.chatid == cs2.chat.currentUser.chatid)) ? "sent" : "replies";
            var content = (this.content !== "") ? '
'+this.content+'' : "";
            var filePreview = (this.type == "file") ? '

File: '+this.meta.fileName+'
' : '';
            var avatarPreview = '';
            if(this.user.profileURL.indexOf("placeholder") > 0) {
                avatarPreview = '
'+this.user.firstName.charAt(0) + this.user.lastName.charAt(0) +'';
            }
            var messageTemplate = $('\

\
                '+avatarPreview+'\

\
                    '+filePreview+'\
                    '+content+'\
                    Sent about '+cs2.chat.timeSince(new Date(this.sent))+' ago\
                \
                 \
            \
            ');
            if(typeof messageTemplate.swipeDetector !== "undefined") {
                messageTemplate.swipeDetector().on("swipeLeft.sd swipeRight.sd", function(event) {
                    if(event.type == "swipeLeft") {
                        cs2.chat.messageList.find("li.swiped").removeClass("swiped");
                        $(event.target).addClass("swiped");
                    }
                    else if(event.type == "swipeRight") {
                        $(event.target).removeClass("swiped");
                    }
                });
            }
            for(var key in this) {
                if(this.hasOwnProperty(key) && typeof this[key] !== 'function') {
                    messageTemplate.data(key, this[key]);
                }
            }
            return messageTemplate;
        }

        this.getFeedHTML = function() {
            var content = (this.content !== "") ? '
'+this.content+'' : "";
            var filePreview = (this.type == "file") ? '

File: '+this.meta.fileName+'
' : '';
            var avatarPreview = '';
            if(this.user.profileURL.indexOf("placeholder") > 0) {
                avatarPreview = '
'+this.user.firstName.charAt(0) + this.user.lastName.charAt(0) +'';
            }
            var messageTemplate = $('\

\

\
                    '+avatarPreview+'\
                \

\

'+this.user.displayName+',  '+cs2.chat.timeSince(new Date(this.sent))+' ago\
                    '+filePreview+'\
                    '+content+'\
                \
            \
            ');
            for(var key in this) {
                if(this.hasOwnProperty(key) && typeof this[key] !== 'function') {
                    messageTemplate.data(key, this[key]);
                }
            }
            return messageTemplate;
        }

        this.setReadStatus = function(status) {
            var body = {
                action:"setChatMessageReadStatus",
                messageId: this.id,
                userChatId: cs2.chat.currentUser.chatid,
                messageStatus: status
            };
            $.ajax({
                method:"POST",
                url:"/ajax/responsive/chats/ajax.cfm",
                data: body,
                dataType: "json"
            });
        }
    }

    cs2.chat.timeSince = function(date) {
        var seconds = Math.floor((new Date() - date) / 1000);
        var interval = Math.floor(seconds / 31536000);

        if (interval > 1) {
            return interval + " years";
        }
        interval = Math.floor(seconds / 2592000);
        if (interval > 1) {
            return interval + " months";
        }
        interval = Math.floor(seconds / 86400);
        if (interval > 1) {
            return interval + " days";
        }
        interval = Math.floor(seconds / 3600);
        if (interval > 1) {
            return interval + " hours";
        }
        interval = Math.floor(seconds / 60);
        if (interval > 1) {
            return interval + " minutes";
        }
        return Math.floor(seconds) + " seconds";
    }

