    cs2.tools.captcha = {
        items:[],
        add:function(id,selector){
            this.items.push({"selector":selector, "id": id});
        },
        reset:function(selector){
            for(var x = 0;x < this.items.length;x++){
                var item = this.items[x];
                if(item.selector == selector){
                    grecaptcha.reset(item.id);
                }
            }
        }
    };
