var dipNavTimeVar_465003 = 1;
var timeClear = 0;
$(document).ready(function() {
 		  
	 $("a.dipLeft").click(function(){
		dipNavigationDelay_465003('p');
	 });
		 
	$("a.dipRight").click(function(){
		dipNavigationDelay_465003('n');
	 });
	
	if($('a.dipNav').length){
	 $("a.dipNav").click(function(){
		var id = $(this).attr('id');
		id = id.replace('nav_465003','');
		dipNavigationDelay_465003(id);
	  });
	}
	 
	 //run slideshow
	 rotateDip_465003(1);
	  
	 //pause slideshow
	 if(!$('#slideControls_465003').length){
		 var slideshowHover
		 if($('#slideshowHover_465003').length){
			 slideshowHover = $('#slideshowHover_465003').val();
		 }else{
			 slideshowHover = 'imageDipShell_465003';
		 }	
		 $('#' + slideshowHover).hover(function(){
			 clearTimeout(slideshowTimer_465003);
			 },function(){
			reRunSlideshow_465003();
		});
	 }
	
	//video hover
	$('.videoShell_465003').hover(function(){
		 clearTimeout(slideshowTimer_465003);
		 },function(){
		reRunSlideshow_465003();
	});
	
	$('.videoPlayerClose').hover(function(){
		clearTimeout(videoVloseTimeOut_465003);
		 $(this).html('Close X');
		 },function(){
		 $(this).html('X');
	});
	
	//slideshow pause controlls
	$('#slideControls_465003').click(function(){
		if(timeClear == 0){
		  clearTimeout(slideshowTimer_465003);
		  timeClear = 1;
		}else{
			reRunSlideshow_465003(1);
			 timeClear = 0;
		}
	});
	
	$('#slideControls_reader_465003').click(function(){
		if(timeClear == 0){
		  clearTimeout(slideshowTimer_465003);
		  timeClear = 1;
		}else{
			reRunSlideshow_465003(1);
			 timeClear = 0;
		}
	});
	
});
	
	//to fix onload run of old slideshow - temp solution
	function runSlideShow465003(){
	}
	
	function reRunSlideshow_465003(sp){
		var currentNum_465003 = document.getElementById('currentNum_465003').value;
		if(sp == 1){
			var time = sp;
		}else{
			var time = document.getElementById('slideshowSpeed_465003').value;
		}
		slideshowTimer_465003 = setTimeout(function(){rotateDip_465003(currentNum_465003);},time);
	}
	
	//slideshow
	function rotateDip_465003(num){
		var time = document.getElementById('slideshowSpeed_465003').value;
		slideshowTimer_465003 = setTimeout("switchDip_465003(" + num + ")",time);
	}
	
	function dipNavigationDelay_465003(nums,speed){
			
			var slideshowMotionSpeed = slideshowMotionSpeed_465003();
			if(speed == '' || speed == null){
				if(slideshowMotionSpeed == '' || slideshowMotionSpeed == null){
					speed = 800;
				}else{
					speed = slideshowMotionSpeed;
				}
			}
			
			if(dipNavTimeVar_465003 == 1){
				dipNavigation_465003(nums,speed);
			}else{
				if(typeof dipNavTime_465003 !== "undefined"){
					clearTimeout(dipNavTime_465003);
				}
				
				dipNavTime_465003 = setTimeout(function(){dipNavigationDelay_465003(nums,speed)},speed);
			}
			timeClear = 1;
	}
	
	function dipNavigation_465003(nums,speed){
		clearTimeout(slideshowTimer_465003);
		
		/*if(typeof slideshowJump_465003 !== "undefined"){
			clearTimeout(slideshowJump_465003);
		}*/
		dipNavTimeVar_465003 = 0;
		var slideshowMotion = slideshowMotion_465003();
		var slideshowMotionSpeed = slideshowMotionSpeed_465003();
		var slideshowQuickNavSpeed = slideshowQuickNavSpeed_465003();
		var complete = document.getElementById('complete_465003').value;
		var currentNum = document.getElementById('currentNum_465003').value;
		var time = document.getElementById('slideshowSpeed_465003').value;
		var totalDips = document.getElementById('totalDips_465003').value;
		var direction = directions_465003(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		var directionSkip = 0;
		
		if(speed == '' || speed == null){
			if(slideshowMotionSpeed == '' || slideshowMotionSpeed == null){
				speed = 800;
			}else{
				speed = slideshowMotionSpeed;
			}
		}
		
		if(currentNum != nums && complete == 1){
		
			if(nums == 'p'){nums = parseInt(currentNum) - 1;}
			if(nums == 'n'){nums = parseInt(currentNum) + 1;}
			nums = parseInt(nums);
			
			if(slideshowMotion != 'f'){
				if(currentNum < nums){directionOn = direction.directionOn;directionOff = direction.directionOff;
				}else{directionOn = direction.directionOff;directionOff = direction.directionOn;}
				//if(currentNum < parseInt(nums) - 1){nums = parseInt(currentNum) + 1;}
				//if(currentNum > parseInt(nums) + 1){nums = parseInt(currentNum) - 1;}
			}
			
			if(nums < 1){nums = totalDips;}
			if(nums > totalDips){nums = 1;}
			
			/*if(!currentNum < parseInt(nums) - 1 && !currentNum > parseInt(nums) + 1){
					newNum = nums;
				}*/
			
			if($('#nav_465003' + currentNum).length){
				$('#nav_465003' + currentNum).removeClass('on');
			}
			if($('#nav_li_465003' + currentNum).length){
				$('#nav_li_465003' + currentNum).removeClass('on');
			}
			if(slideshowMotion != 'f'){
				$('#dipImage_465003' + currentNum).hide("slide", { direction: directionOff }, speed);
			}else{
				$('#dipImage_465003' + currentNum).fadeOut(speed);
			}

			if($('#videoShell_465003' + currentNum).length){
					closePlayer_465003(currentNum);
					$('#videoShell_465003' + currentNum).fadeOut(speed);
				}
			if($('#dipContent_465003' + currentNum).length){
				$('#dipContent_465003' + currentNum).fadeOut(speed);
			}
			
			if($('#nav_465003' + currentNum).length){
				$('#nav_465003' + (nums)).addClass('on');
			}
			if($('#nav_li_465003' + currentNum).length){
				$('#nav_li_465003' + (nums)).addClass('on');
			}
			if(slideshowMotion != 'f'){
				$('#dipImage_465003' + (nums)).show("slide", { direction: directionOn }, speed, function(){
					document.getElementById('complete_465003').value = 1;
					dipNavTimeVar_465003 = 1;
				});
			}else{
				$('#dipImage_465003' + (nums)).fadeIn(speed);
				dipNavTimeVar_465003 = 1;
			}
			if($('#videoShell_465003' + nums).length){
					$('#videoShell_465003' + nums).fadeIn(speed);
				}
			if($('#dipContent_465003' + nums).length){
				$('#dipContent_465003' + (nums)).fadeIn(speed);
			}
			
			document.getElementById('currentNum_465003').value = nums;
			//slideshowTimer=setTimeout("switchDip_465003(" + nums + ")",time);
			//currentNum = document.getElementById('currentNum_465003').value;
			
			/*if(slideshowMotion != 'f'){
				if(currentNum < (nums - 1) && currentNum != totalDips){
					slideshowJump_465003=setTimeout("dipNavigation_465003(" + (nums + 1) + "," + slideshowQuickNavSpeed + ")",speed);
				}
				if(currentNum > (nums + 1)){
					slideshowJump_465003=setTimeout("dipNavigation_465003(" + (nums - 1) + "," + slideshowQuickNavSpeed + ")",speed);
				}
			}*/
			//dipNavTimeVar_465003 = 1;
		}
	}
	
	function switchDip_465003(num,pause){
		clearTimeout(slideshowTimer_465003);
		var timer;
		var nums = num;
		var slideshowMotion = slideshowMotion_465003();
		var speed = slideshowMotionSpeed_465003();
		var currentNum = document.getElementById('currentNum_465003').value;
		var totalDips = document.getElementById('totalDips_465003').value;
		var video = document.getElementById('video_465003').value;
		if(speed == '' || speed == null){
			speed = 800;
		}
		
		var direction = directions_465003(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		
		if(totalDips > 1 && video == 0){
		
			if(nums < 1){nums = 1;}
			if(nums > totalDips){nums = totalDips;}
			
			if($('#nav_465003' + nums).length){
				$('#nav_465003' + nums).removeClass('on');
			}
			
			if($('#nav_li_465003' + nums).length){
				$('#nav_li_465003' + nums).removeClass('on');
			}
			
			if(slideshowMotion != 'f'){
				if($('#videoShell_465003' + nums).length){
					$('#videoShell_465003' + nums).hide();
				}
				$('#dipImage_465003' + nums).hide("slide", { direction: directionOff }, speed);
			}else{
				$('#dipImage_465003' + nums).fadeOut(speed);
			}
			
			if($('#videoShell_465003' + nums).length){
				$('#videoShell_465003' + nums).fadeOut(speed);
			}
			if($('#dipContent_465003' + nums).length){
				$('#dipContent_465003' + nums).fadeOut(speed);
			}
			
			if(nums >= totalDips){nums = 1;}else{nums = nums + 1;}
			
			if($('#nav_465003' + nums).length){
				$('#nav_465003' + nums).addClass('on');
			}
			
			if($('#nav_li_465003' + nums).length){
				$('#nav_li_465003' + nums).addClass('on');
			}
			
			if(slideshowMotion != 'f'){
				$('#dipImage_465003' + nums).show("slide", { direction: directionOn }, speed, function(){
				if($('#videoShell_465003' + nums).length){
					$('#videoShell_465003' + nums).fadeIn(speed);
					}	
				});
			}else{
				$('#dipImage_465003' + (nums)).fadeIn(speed);
			}
			
			if(slideshowMotion == 'f'){
				if($('#videoShell_465003' + nums).length){
					$('#videoShell_465003' + nums).fadeIn(speed);
				}
			}
			if($('#dipContent_465003' + nums).length){
				$('#dipContent_465003' + (nums)).fadeIn(speed);
			}
				
			if(num < totalDips){timer = num + 1;}else{timer = 1;}
			rotateDip_465003(timer);
			document.getElementById('currentNum_465003').value = nums;
		}
	}
	
	function slideshowMotion_465003(){
		if($('#slideshowMotion_465003').length){
			slideshowMotion = document.getElementById('slideshowMotion_465003').value;
		}else{
			slideshowMotion = 'f';
		}
		if(slideshowMotion == ''){slideshowMotion = 'f'}
		return slideshowMotion;
	}
	
	function slideshowMotionSpeed_465003(){
		if($('#slideshowMotionSpeed_465003').length){
			slideshowMotionSpeed = document.getElementById('slideshowMotionSpeed_465003').value;
		}else{
			slideshowMotionSpeed = '';
		}
		return slideshowMotionSpeed;
	}
	
	
	function slideshowQuickNavSpeed_465003(){
		if($('#slideshowQuickNavSpeed_465003').length){
			slideshowQuickNavSpeed = document.getElementById('slideshowQuickNavSpeed_465003').value;
		}else{
			slideshowQuickNavSpeed = 50;
		}
	
		return slideshowQuickNavSpeed;
	}
	
	//motion direction
	function directions_465003(slideshowMotion){
		switch(slideshowMotion){
			case 'sr':
			directionOn = 'left';
			directionOff = 'right';
			break;
			case 'su':
			directionOn = 'down';
			directionOff = 'up';
			break;
			case 'sd':
			directionOn = 'up';
			directionOff = 'down';
			break;
			default:
			directionOn = 'right';
			directionOff = 'left';
		}
		
		return {directionOn: directionOn, directionOff: directionOff};
	}

	//video
	function playvideo_465003(num){
		getVideoiframeSrc_465003(num);
		$('#videoPlayerClose_465003' + num).html('Close X');
		$('#videoPlayer_465003' + num).fadeIn(800);
		$('#video_465003').val(1);
		videoVloseTimeOut_465003 = setTimeout("$('#videoPlayerClose_465003" + num + "').html('X')",2500);
	}
	
	function closePlayer_465003(num){
		$('#videoPlayer_465003' + num).fadeOut(200);
		$('#video_465003').val(0);
		var video = $('#VideoPlayback_465003' + num).attr("src");
		$('#VideoPlayback_465003' + num).attr("src","");
		$('#VideoPlayback_465003' + num).attr("src",video);
		reRunSlideshow_465003();
	}
	
	function getVideoiframeSrc_465003(num){
		var iframeSrc;
		if($('.Videoiframe_465003' + num).length){
			iframeSrc = $('.Videoiframe_465003' + num).attr('src');
			var n = iframeSrc.indexOf("?");
			if(n != -1){
				$('.Videoiframe_465003' + num).attr('src',iframeSrc + '&wmode=opaque');
			}else{
				$('.Videoiframe_465003' + num).attr('src',iframeSrc + '?wmode=opaque');
			}
			
		}
	}

