	function slideshowMotion_465005(){
		if($('#slideshowMotion_465005').length){
			slideshowMotion = document.getElementById('slideshowMotion_465005').value;
		}else{
			slideshowMotion = 'f';
		}
		if(slideshowMotion == ''){slideshowMotion = 'f'}
		return slideshowMotion;
	}
	
	function slideshowMotionSpeed_465005(){
		if($('#slideshowMotionSpeed_465005').length){
			slideshowMotionSpeed = document.getElementById('slideshowMotionSpeed_465005').value;
		}else{
			slideshowMotionSpeed = '';
		}
		return slideshowMotionSpeed;
	}
	
	
	function slideshowQuickNavSpeed_465005(){
		if($('#slideshowQuickNavSpeed_465005').length){
			slideshowQuickNavSpeed = document.getElementById('slideshowQuickNavSpeed_465005').value;
		}else{
			slideshowQuickNavSpeed = 50;
		}
	
		return slideshowQuickNavSpeed;
	}
	
	//motion direction
	function directions_465005(slideshowMotion){
		switch(slideshowMotion){
			case 'sr':
			directionOn = 'left';
			directionOff = 'right';
			break;
			case 'su':
			directionOn = 'down';
			directionOff = 'up';
			break;
			case 'sd':
			directionOn = 'up';
			directionOff = 'down';
			break;
			default:
			directionOn = 'right';
			directionOff = 'left';
		}
		
		return {directionOn: directionOn, directionOff: directionOff};
	}

	//video
	function playvideo_465005(num){
		getVideoiframeSrc_465005(num);
		$('#videoPlayerClose_465005' + num).html('Close X');
		$('#videoPlayer_465005' + num).fadeIn(800);
		$('#video_465005').val(1);
		videoVloseTimeOut_465005 = setTimeout("$('#videoPlayerClose_465005" + num + "').html('X')",2500);
	}
	
	function closePlayer_465005(num){
		$('#videoPlayer_465005' + num).fadeOut(200);
		$('#video_465005').val(0);
		var video = $('#VideoPlayback_465005' + num).attr("src");
		$('#VideoPlayback_465005' + num).attr("src","");
		$('#VideoPlayback_465005' + num).attr("src",video);
		reRunSlideshow_465005();
	}
	
	function getVideoiframeSrc_465005(num){
		var iframeSrc;
		if($('.Videoiframe_465005' + num).length){
			iframeSrc = $('.Videoiframe_465005' + num).attr('src');
			var n = iframeSrc.indexOf("?");
			if(n != -1){
				$('.Videoiframe_465005' + num).attr('src',iframeSrc + '&wmode=opaque');
			}else{
				$('.Videoiframe_465005' + num).attr('src',iframeSrc + '?wmode=opaque');
			}
			
		}
	}

div.script:
var dipNavTimeVar_466065 = 1;
var timeClear = 0;
$(document).ready(function() {
 		  
	 $("a.dipLeft").click(function(){
		dipNavigationDelay_466065('p');
	 });
		 
	$("a.dipRight").click(function(){
		dipNavigationDelay_466065('n');
	 });
	
	if($('a.dipNav').length){
	 $("a.dipNav").click(function(){
		var id = $(this).attr('id');
		id = id.replace('nav_466065','');
		dipNavigationDelay_466065(id);
	  });
	}
	 
	 //run slideshow
	 rotateDip_466065(1);
	  
	 //pause slideshow
	 if(!$('#slideControls_466065').length){
		 var slideshowHover
		 if($('#slideshowHover_466065').length){
			 slideshowHover = $('#slideshowHover_466065').val();
		 }else{
			 slideshowHover = 'imageDipShell_466065';
		 }	
		 $('#' + slideshowHover).hover(function(){
			 clearTimeout(slideshowTimer_466065);
			 },function(){
			reRunSlideshow_466065();
		});
	 }
	
	//video hover
	$('.videoShell_466065').hover(function(){
		 clearTimeout(slideshowTimer_466065);
		 },function(){
		reRunSlideshow_466065();
	});
	
	$('.videoPlayerClose').hover(function(){
		clearTimeout(videoVloseTimeOut_466065);
		 $(this).html('Close X');
		 },function(){
		 $(this).html('X');
	});
	
	//slideshow pause controlls
	$('#slideControls_466065').click(function(){
		if(timeClear == 0){
		  clearTimeout(slideshowTimer_466065);
		  timeClear = 1;
		}else{
			reRunSlideshow_466065(1);
			 timeClear = 0;
		}
	});
	
	$('#slideControls_reader_466065').click(function(){
		if(timeClear == 0){
		  clearTimeout(slideshowTimer_466065);
		  timeClear = 1;
		}else{
			reRunSlideshow_466065(1);
			 timeClear = 0;
		}
	});
	
});
	
