	}
	
	function reRunSlideshow_466065(sp){
		var currentNum_466065 = document.getElementById('currentNum_466065').value;
		if(sp == 1){
			var time = sp;
		}else{
			var time = document.getElementById('slideshowSpeed_466065').value;
		}
		slideshowTimer_466065 = setTimeout(function(){rotateDip_466065(currentNum_466065);},time);
	}
	
	//slideshow
	function rotateDip_466065(num){
		var time = document.getElementById('slideshowSpeed_466065').value;
		slideshowTimer_466065 = setTimeout("switchDip_466065(" + num + ")",time);
	}
	
	function dipNavigationDelay_466065(nums,speed){
			
			var slideshowMotionSpeed = slideshowMotionSpeed_466065();
			if(speed == '' || speed == null){
				if(slideshowMotionSpeed == '' || slideshowMotionSpeed == null){
					speed = 800;
				}else{
					speed = slideshowMotionSpeed;
				}
			}
			
			if(dipNavTimeVar_466065 == 1){
				dipNavigation_466065(nums,speed);
			}else{
				if(typeof dipNavTime_466065 !== "undefined"){
					clearTimeout(dipNavTime_466065);
				}
				
				dipNavTime_466065 = setTimeout(function(){dipNavigationDelay_466065(nums,speed)},speed);
			}
			timeClear = 1;
	}
	
	function dipNavigation_466065(nums,speed){
		clearTimeout(slideshowTimer_466065);
		
		/*if(typeof slideshowJump_466065 !== "undefined"){
			clearTimeout(slideshowJump_466065);
		}*/
		dipNavTimeVar_466065 = 0;
		var slideshowMotion = slideshowMotion_466065();
		var slideshowMotionSpeed = slideshowMotionSpeed_466065();
		var slideshowQuickNavSpeed = slideshowQuickNavSpeed_466065();
		var complete = document.getElementById('complete_466065').value;
		var currentNum = document.getElementById('currentNum_466065').value;
		var time = document.getElementById('slideshowSpeed_466065').value;
		var totalDips = document.getElementById('totalDips_466065').value;
		var direction = directions_466065(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		var directionSkip = 0;
		
		if(speed == '' || speed == null){
			if(slideshowMotionSpeed == '' || slideshowMotionSpeed == null){
				speed = 800;
			}else{
				speed = slideshowMotionSpeed;
			}
		}
		
		if(currentNum != nums && complete == 1){
		
			if(nums == 'p'){nums = parseInt(currentNum) - 1;}
			if(nums == 'n'){nums = parseInt(currentNum) + 1;}
			nums = parseInt(nums);
			
			if(slideshowMotion != 'f'){
				if(currentNum < nums){directionOn = direction.directionOn;directionOff = direction.directionOff;
				}else{directionOn = direction.directionOff;directionOff = direction.directionOn;}
				//if(currentNum < parseInt(nums) - 1){nums = parseInt(currentNum) + 1;}
				//if(currentNum > parseInt(nums) + 1){nums = parseInt(currentNum) - 1;}
			}
			
			if(nums < 1){nums = totalDips;}
			if(nums > totalDips){nums = 1;}
			
			/*if(!currentNum < parseInt(nums) - 1 && !currentNum > parseInt(nums) + 1){
					newNum = nums;
				}*/
			
			if($('#nav_466065' + currentNum).length){
				$('#nav_466065' + currentNum).removeClass('on');
			}
			if($('#nav_li_466065' + currentNum).length){
				$('#nav_li_466065' + currentNum).removeClass('on');
			}
			if(slideshowMotion != 'f'){
				$('#dipImage_466065' + currentNum).hide("slide", { direction: directionOff }, speed);
			}else{
				$('#dipImage_466065' + currentNum).fadeOut(speed);
			}

			if($('#videoShell_466065' + currentNum).length){
					closePlayer_466065(currentNum);
					$('#videoShell_466065' + currentNum).fadeOut(speed);
				}
			if($('#dipContent_466065' + currentNum).length){
				$('#dipContent_466065' + currentNum).fadeOut(speed);
			}
			
			if($('#nav_466065' + currentNum).length){
				$('#nav_466065' + (nums)).addClass('on');
			}
			if($('#nav_li_466065' + currentNum).length){
				$('#nav_li_466065' + (nums)).addClass('on');
			}
			if(slideshowMotion != 'f'){
				$('#dipImage_466065' + (nums)).show("slide", { direction: directionOn }, speed, function(){
					document.getElementById('complete_466065').value = 1;
					dipNavTimeVar_466065 = 1;
				});
			}else{
				$('#dipImage_466065' + (nums)).fadeIn(speed);
				dipNavTimeVar_466065 = 1;
			}
			if($('#videoShell_466065' + nums).length){
					$('#videoShell_466065' + nums).fadeIn(speed);
				}
			if($('#dipContent_466065' + nums).length){
				$('#dipContent_466065' + (nums)).fadeIn(speed);
			}
			
			document.getElementById('currentNum_466065').value = nums;
			//slideshowTimer=setTimeout("switchDip_466065(" + nums + ")",time);
			//currentNum = document.getElementById('currentNum_466065').value;
			
			/*if(slideshowMotion != 'f'){
				if(currentNum < (nums - 1) && currentNum != totalDips){
					slideshowJump_466065=setTimeout("dipNavigation_466065(" + (nums + 1) + "," + slideshowQuickNavSpeed + ")",speed);
				}
				if(currentNum > (nums + 1)){
					slideshowJump_466065=setTimeout("dipNavigation_466065(" + (nums - 1) + "," + slideshowQuickNavSpeed + ")",speed);
				}
			}*/
			//dipNavTimeVar_466065 = 1;
		}
	}
	
	function switchDip_466065(num,pause){
		clearTimeout(slideshowTimer_466065);
		var timer;
		var nums = num;
		var slideshowMotion = slideshowMotion_466065();
		var speed = slideshowMotionSpeed_466065();
		var currentNum = document.getElementById('currentNum_466065').value;
		var totalDips = document.getElementById('totalDips_466065').value;
		var video = document.getElementById('video_466065').value;
		if(speed == '' || speed == null){
			speed = 800;
		}
		
		var direction = directions_466065(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		
		if(totalDips > 1 && video == 0){
		
			if(nums < 1){nums = 1;}
			if(nums > totalDips){nums = totalDips;}
			
			if($('#nav_466065' + nums).length){
				$('#nav_466065' + nums).removeClass('on');
			}
			
			if($('#nav_li_466065' + nums).length){
				$('#nav_li_466065' + nums).removeClass('on');
			}
			
			if(slideshowMotion != 'f'){
				if($('#videoShell_466065' + nums).length){
					$('#videoShell_466065' + nums).hide();
				}
				$('#dipImage_466065' + nums).hide("slide", { direction: directionOff }, speed);
			}else{
				$('#dipImage_466065' + nums).fadeOut(speed);
			}
			
			if($('#videoShell_466065' + nums).length){
				$('#videoShell_466065' + nums).fadeOut(speed);
			}
			if($('#dipContent_466065' + nums).length){
				$('#dipContent_466065' + nums).fadeOut(speed);
			}
			
			if(nums >= totalDips){nums = 1;}else{nums = nums + 1;}
			
			if($('#nav_466065' + nums).length){
				$('#nav_466065' + nums).addClass('on');
			}
			
			if($('#nav_li_466065' + nums).length){
				$('#nav_li_466065' + nums).addClass('on');
			}
			
			if(slideshowMotion != 'f'){
				$('#dipImage_466065' + nums).show("slide", { direction: directionOn }, speed, function(){
				if($('#videoShell_466065' + nums).length){
					$('#videoShell_466065' + nums).fadeIn(speed);
					}	
				});
			}else{
				$('#dipImage_466065' + (nums)).fadeIn(speed);
			}
			
			if(slideshowMotion == 'f'){
				if($('#videoShell_466065' + nums).length){
					$('#videoShell_466065' + nums).fadeIn(speed);
				}
			}
			if($('#dipContent_466065' + nums).length){
				$('#dipContent_466065' + (nums)).fadeIn(speed);
			}
				
			if(num < totalDips){timer = num + 1;}else{timer = 1;}
			rotateDip_466065(timer);
			document.getElementById('currentNum_466065').value = nums;
		}
	}
	
	function slideshowMotion_466065(){
		if($('#slideshowMotion_466065').length){
			slideshowMotion = document.getElementById('slideshowMotion_466065').value;
		}else{
			slideshowMotion = 'f';
		}
		if(slideshowMotion == ''){slideshowMotion = 'f'}
		return slideshowMotion;
	}
	
