	function switchDip_466066(num,pause){
		clearTimeout(slideshowTimer_466066);
		var timer;
		var nums = num;
		var slideshowMotion = slideshowMotion_466066();
		var speed = slideshowMotionSpeed_466066();
		var currentNum = document.getElementById('currentNum_466066').value;
		var totalDips = document.getElementById('totalDips_466066').value;
		var video = document.getElementById('video_466066').value;
		if(speed == '' || speed == null){
			speed = 800;
		}
		
		var direction = directions_466066(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		
		if(totalDips > 1 && video == 0){
		
			if(nums < 1){nums = 1;}
			if(nums > totalDips){nums = totalDips;}
			
			if($('#nav_466066' + nums).length){
				$('#nav_466066' + nums).removeClass('on');
			}
			
			if($('#nav_li_466066' + nums).length){
				$('#nav_li_466066' + nums).removeClass('on');
			}
			
			if(slideshowMotion != 'f'){
				if($('#videoShell_466066' + nums).length){
					$('#videoShell_466066' + nums).hide();
				}
				$('#dipImage_466066' + nums).hide("slide", { direction: directionOff }, speed);
			}else{
				$('#dipImage_466066' + nums).fadeOut(speed);
			}
			
			if($('#videoShell_466066' + nums).length){
				$('#videoShell_466066' + nums).fadeOut(speed);
			}
			if($('#dipContent_466066' + nums).length){
				$('#dipContent_466066' + nums).fadeOut(speed);
			}
			
			if(nums >= totalDips){nums = 1;}else{nums = nums + 1;}
			
			if($('#nav_466066' + nums).length){
				$('#nav_466066' + nums).addClass('on');
			}
			
			if($('#nav_li_466066' + nums).length){
				$('#nav_li_466066' + nums).addClass('on');
			}
			
			if(slideshowMotion != 'f'){
				$('#dipImage_466066' + nums).show("slide", { direction: directionOn }, speed, function(){
				if($('#videoShell_466066' + nums).length){
					$('#videoShell_466066' + nums).fadeIn(speed);
					}	
				});
			}else{
				$('#dipImage_466066' + (nums)).fadeIn(speed);
			}
			
			if(slideshowMotion == 'f'){
				if($('#videoShell_466066' + nums).length){
					$('#videoShell_466066' + nums).fadeIn(speed);
				}
			}
			if($('#dipContent_466066' + nums).length){
				$('#dipContent_466066' + (nums)).fadeIn(speed);
			}
				
			if(num < totalDips){timer = num + 1;}else{timer = 1;}
			rotateDip_466066(timer);
			document.getElementById('currentNum_466066').value = nums;
		}
	}
	
	function slideshowMotion_466066(){
		if($('#slideshowMotion_466066').length){
			slideshowMotion = document.getElementById('slideshowMotion_466066').value;
		}else{
			slideshowMotion = 'f';
		}
		if(slideshowMotion == ''){slideshowMotion = 'f'}
		return slideshowMotion;
	}
	
	function slideshowMotionSpeed_466066(){
		if($('#slideshowMotionSpeed_466066').length){
			slideshowMotionSpeed = document.getElementById('slideshowMotionSpeed_466066').value;
		}else{
			slideshowMotionSpeed = '';
		}
		return slideshowMotionSpeed;
	}
	
	
	function slideshowQuickNavSpeed_466066(){
		if($('#slideshowQuickNavSpeed_466066').length){
			slideshowQuickNavSpeed = document.getElementById('slideshowQuickNavSpeed_466066').value;
		}else{
			slideshowQuickNavSpeed = 50;
		}
	
		return slideshowQuickNavSpeed;
	}
	
	//motion direction
	function directions_466066(slideshowMotion){
		switch(slideshowMotion){
			case 'sr':
			directionOn = 'left';
			directionOff = 'right';
			break;
			case 'su':
			directionOn = 'down';
			directionOff = 'up';
			break;
			case 'sd':
			directionOn = 'up';
			directionOff = 'down';
			break;
			default:
			directionOn = 'right';
			directionOff = 'left';
		}
		
		return {directionOn: directionOn, directionOff: directionOff};
	}

	//video
	function playvideo_466066(num){
		getVideoiframeSrc_466066(num);
		$('#videoPlayerClose_466066' + num).html('Close X');
		$('#videoPlayer_466066' + num).fadeIn(800);
		$('#video_466066').val(1);
		videoVloseTimeOut_466066 = setTimeout("$('#videoPlayerClose_466066" + num + "').html('X')",2500);
	}
	
	function closePlayer_466066(num){
		$('#videoPlayer_466066' + num).fadeOut(200);
		$('#video_466066').val(0);
		var video = $('#VideoPlayback_466066' + num).attr("src");
		$('#VideoPlayback_466066' + num).attr("src","");
		$('#VideoPlayback_466066' + num).attr("src",video);
		reRunSlideshow_466066();
	}
	
	function getVideoiframeSrc_466066(num){
		var iframeSrc;
		if($('.Videoiframe_466066' + num).length){
			iframeSrc = $('.Videoiframe_466066' + num).attr('src');
			var n = iframeSrc.indexOf("?");
			if(n != -1){
				$('.Videoiframe_466066' + num).attr('src',iframeSrc + '&wmode=opaque');
			}else{
				$('.Videoiframe_466066' + num).attr('src',iframeSrc + '?wmode=opaque');
			}
			
		}
	}

