var dipNavTimeVar_465011 = 1;
var timeClear = 0;
$(document).ready(function() {
 		  
	 $("a.dipLeft").click(function(){
		dipNavigationDelay_465011('p');
	 });
		 
	$("a.dipRight").click(function(){
		dipNavigationDelay_465011('n');
	 });
	
	if($('a.dipNav').length){
	 $("a.dipNav").click(function(){
		var id = $(this).attr('id');
		id = id.replace('nav_465011','');
		dipNavigationDelay_465011(id);
	  });
	}
	 
	 //run slideshow
	 rotateDip_465011(1);
	  
	 //pause slideshow
	 if(!$('#slideControls_465011').length){
		 var slideshowHover
		 if($('#slideshowHover_465011').length){
			 slideshowHover = $('#slideshowHover_465011').val();
		 }else{
			 slideshowHover = 'imageDipShell_465011';
		 }	
		 $('#' + slideshowHover).hover(function(){
			 clearTimeout(slideshowTimer_465011);
			 },function(){
			reRunSlideshow_465011();
		});
	 }
	
	//video hover
	$('.videoShell_465011').hover(function(){
		 clearTimeout(slideshowTimer_465011);
		 },function(){
		reRunSlideshow_465011();
	});
	
	$('.videoPlayerClose').hover(function(){
		clearTimeout(videoVloseTimeOut_465011);
		 $(this).html('Close X');
		 },function(){
		 $(this).html('X');
	});
	
	//slideshow pause controlls
	$('#slideControls_465011').click(function(){
		if(timeClear == 0){
		  clearTimeout(slideshowTimer_465011);
		  timeClear = 1;
		}else{
			reRunSlideshow_465011(1);
			 timeClear = 0;
		}
	});
	
	$('#slideControls_reader_465011').click(function(){
		if(timeClear == 0){
		  clearTimeout(slideshowTimer_465011);
		  timeClear = 1;
		}else{
			reRunSlideshow_465011(1);
			 timeClear = 0;
		}
	});
	
});
	
