	function reRunSlideshow_465011(sp){
		var currentNum_465011 = document.getElementById('currentNum_465011').value;
		if(sp == 1){
			var time = sp;
		}else{
			var time = document.getElementById('slideshowSpeed_465011').value;
		}
		slideshowTimer_465011 = setTimeout(function(){rotateDip_465011(currentNum_465011);},time);
	}
	
	//slideshow
	function rotateDip_465011(num){
		var time = document.getElementById('slideshowSpeed_465011').value;
		slideshowTimer_465011 = setTimeout("switchDip_465011(" + num + ")",time);
	}
	
	function dipNavigationDelay_465011(nums,speed){
			
			var slideshowMotionSpeed = slideshowMotionSpeed_465011();
			if(speed == '' || speed == null){
				if(slideshowMotionSpeed == '' || slideshowMotionSpeed == null){
					speed = 800;
				}else{
					speed = slideshowMotionSpeed;
				}
			}
			
			if(dipNavTimeVar_465011 == 1){
				dipNavigation_465011(nums,speed);
			}else{
				if(typeof dipNavTime_465011 !== "undefined"){
					clearTimeout(dipNavTime_465011);
				}
				
				dipNavTime_465011 = setTimeout(function(){dipNavigationDelay_465011(nums,speed)},speed);
			}
			timeClear = 1;
	}
	
	function dipNavigation_465011(nums,speed){
		clearTimeout(slideshowTimer_465011);
		
		/*if(typeof slideshowJump_465011 !== "undefined"){
			clearTimeout(slideshowJump_465011);
		}*/
		dipNavTimeVar_465011 = 0;
		var slideshowMotion = slideshowMotion_465011();
		var slideshowMotionSpeed = slideshowMotionSpeed_465011();
		var slideshowQuickNavSpeed = slideshowQuickNavSpeed_465011();
		var complete = document.getElementById('complete_465011').value;
		var currentNum = document.getElementById('currentNum_465011').value;
		var time = document.getElementById('slideshowSpeed_465011').value;
		var totalDips = document.getElementById('totalDips_465011').value;
		var direction = directions_465011(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		var directionSkip = 0;
		
		if(speed == '' || speed == null){
			if(slideshowMotionSpeed == '' || slideshowMotionSpeed == null){
				speed = 800;
			}else{
				speed = slideshowMotionSpeed;
			}
		}
		
		if(currentNum != nums && complete == 1){
		
			if(nums == 'p'){nums = parseInt(currentNum) - 1;}
			if(nums == 'n'){nums = parseInt(currentNum) + 1;}
			nums = parseInt(nums);
			
			if(slideshowMotion != 'f'){
				if(currentNum < nums){directionOn = direction.directionOn;directionOff = direction.directionOff;
				}else{directionOn = direction.directionOff;directionOff = direction.directionOn;}
				//if(currentNum < parseInt(nums) - 1){nums = parseInt(currentNum) + 1;}
				//if(currentNum > parseInt(nums) + 1){nums = parseInt(currentNum) - 1;}
			}
			
			if(nums < 1){nums = totalDips;}
			if(nums > totalDips){nums = 1;}
			
			/*if(!currentNum < parseInt(nums) - 1 && !currentNum > parseInt(nums) + 1){
					newNum = nums;
				}*/
			
			if($('#nav_465011' + currentNum).length){
				$('#nav_465011' + currentNum).removeClass('on');
			}
			if($('#nav_li_465011' + currentNum).length){
				$('#nav_li_465011' + currentNum).removeClass('on');
			}
			if(slideshowMotion != 'f'){
				$('#dipImage_465011' + currentNum).hide("slide", { direction: directionOff }, speed);
			}else{
				$('#dipImage_465011' + currentNum).fadeOut(speed);
			}

			if($('#videoShell_465011' + currentNum).length){
					closePlayer_465011(currentNum);
					$('#videoShell_465011' + currentNum).fadeOut(speed);
				}
			if($('#dipContent_465011' + currentNum).length){
				$('#dipContent_465011' + currentNum).fadeOut(speed);
			}
			
			if($('#nav_465011' + currentNum).length){
				$('#nav_465011' + (nums)).addClass('on');
			}
			if($('#nav_li_465011' + currentNum).length){
				$('#nav_li_465011' + (nums)).addClass('on');
			}
			if(slideshowMotion != 'f'){
				$('#dipImage_465011' + (nums)).show("slide", { direction: directionOn }, speed, function(){
					document.getElementById('complete_465011').value = 1;
					dipNavTimeVar_465011 = 1;
				});
			}else{
				$('#dipImage_465011' + (nums)).fadeIn(speed);
				dipNavTimeVar_465011 = 1;
			}
			if($('#videoShell_465011' + nums).length){
					$('#videoShell_465011' + nums).fadeIn(speed);
				}
			if($('#dipContent_465011' + nums).length){
				$('#dipContent_465011' + (nums)).fadeIn(speed);
			}
			
			document.getElementById('currentNum_465011').value = nums;
			//slideshowTimer=setTimeout("switchDip_465011(" + nums + ")",time);
			//currentNum = document.getElementById('currentNum_465011').value;
			
			/*if(slideshowMotion != 'f'){
				if(currentNum < (nums - 1) && currentNum != totalDips){
					slideshowJump_465011=setTimeout("dipNavigation_465011(" + (nums + 1) + "," + slideshowQuickNavSpeed + ")",speed);
				}
				if(currentNum > (nums + 1)){
					slideshowJump_465011=setTimeout("dipNavigation_465011(" + (nums - 1) + "," + slideshowQuickNavSpeed + ")",speed);
				}
			}*/
			//dipNavTimeVar_465011 = 1;
		}
	}
	
	function switchDip_465011(num,pause){
		clearTimeout(slideshowTimer_465011);
		var timer;
		var nums = num;
		var slideshowMotion = slideshowMotion_465011();
		var speed = slideshowMotionSpeed_465011();
		var currentNum = document.getElementById('currentNum_465011').value;
		var totalDips = document.getElementById('totalDips_465011').value;
		var video = document.getElementById('video_465011').value;
		if(speed == '' || speed == null){
			speed = 800;
		}
		
		var direction = directions_465011(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		
		if(totalDips > 1 && video == 0){
		
			if(nums < 1){nums = 1;}
			if(nums > totalDips){nums = totalDips;}
			
