				if($('#videoShell_465011' + nums).length){
					$('#videoShell_465011' + nums).hide();
				}
				$('#dipImage_465011' + nums).hide("slide", { direction: directionOff }, speed);
			}else{
				$('#dipImage_465011' + nums).fadeOut(speed);
			}
			
			if($('#videoShell_465011' + nums).length){
				$('#videoShell_465011' + nums).fadeOut(speed);
			}
			if($('#dipContent_465011' + nums).length){
				$('#dipContent_465011' + nums).fadeOut(speed);
			}
			
			if(nums >= totalDips){nums = 1;}else{nums = nums + 1;}
			
			if($('#nav_465011' + nums).length){
				$('#nav_465011' + nums).addClass('on');
			}
			
			if($('#nav_li_465011' + nums).length){
				$('#nav_li_465011' + nums).addClass('on');
