	function switchDip_468105(num,pause){
		clearTimeout(slideshowTimer_468105);
		var timer;
		var nums = num;
		var slideshowMotion = slideshowMotion_468105();
		var speed = slideshowMotionSpeed_468105();
		var currentNum = document.getElementById('currentNum_468105').value;
		var totalDips = document.getElementById('totalDips_468105').value;
		var video = document.getElementById('video_468105').value;
		if(speed == '' || speed == null){
			speed = 800;
		}
		
		var direction = directions_468105(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		
		if(totalDips > 1 && video == 0){
		
			if(nums < 1){nums = 1;}
			if(nums > totalDips){nums = totalDips;}
			
			if($('#nav_468105' + nums).length){
				$('#nav_468105' + nums).removeClass('on');
			}
			
			if($('#nav_li_468105' + nums).length){
				$('#nav_li_468105' + nums).removeClass('on');
			}
			
			if(slideshowMotion != 'f'){
				if($('#videoShell_468105' + nums).length){
					$('#videoShell_468105' + nums).hide();
				}
				$('#dipImage_468105' + nums).hide("slide", { direction: directionOff }, speed);
			}else{
				$('#dipImage_468105' + nums).fadeOut(speed);
			}
			
			if($('#videoShell_468105' + nums).length){
				$('#videoShell_468105' + nums).fadeOut(speed);
			}
			if($('#dipContent_468105' + nums).length){
				$('#dipContent_468105' + nums).fadeOut(speed);
			}
			
			if(nums >= totalDips){nums = 1;}else{nums = nums + 1;}
			
			if($('#nav_468105' + nums).length){
				$('#nav_468105' + nums).addClass('on');
			}
			
			if($('#nav_li_468105' + nums).length){
				$('#nav_li_468105' + nums).addClass('on');
			}
			
			if(slideshowMotion != 'f'){
				$('#dipImage_468105' + nums).show("slide", { direction: directionOn }, speed, function(){
				if($('#videoShell_468105' + nums).length){
					$('#videoShell_468105' + nums).fadeIn(speed);
					}	
				});
			}else{
				$('#dipImage_468105' + (nums)).fadeIn(speed);
			}
			
			if(slideshowMotion == 'f'){
				if($('#videoShell_468105' + nums).length){
					$('#videoShell_468105' + nums).fadeIn(speed);
				}
			}
			if($('#dipContent_468105' + nums).length){
				$('#dipContent_468105' + (nums)).fadeIn(speed);
			}
				
			if(num < totalDips){timer = num + 1;}else{timer = 1;}
			rotateDip_468105(timer);
			document.getElementById('currentNum_468105').value = nums;
		}
	}
	
	function slideshowMotion_468105(){
		if($('#slideshowMotion_468105').length){
			slideshowMotion = document.getElementById('slideshowMotion_468105').value;
		}else{
			slideshowMotion = 'f';
		}
		if(slideshowMotion == ''){slideshowMotion = 'f'}
		return slideshowMotion;
	}
	
	function slideshowMotionSpeed_468105(){
		if($('#slideshowMotionSpeed_468105').length){
			slideshowMotionSpeed = document.getElementById('slideshowMotionSpeed_468105').value;
		}else{
			slideshowMotionSpeed = '';
		}
		return slideshowMotionSpeed;
	}
	
	
	function slideshowQuickNavSpeed_468105(){
		if($('#slideshowQuickNavSpeed_468105').length){
			slideshowQuickNavSpeed = document.getElementById('slideshowQuickNavSpeed_468105').value;
		}else{
			slideshowQuickNavSpeed = 50;
		}
	
		return slideshowQuickNavSpeed;
	}
	
	//motion direction
	function directions_468105(slideshowMotion){
		switch(slideshowMotion){
			case 'sr':
			directionOn = 'left';
			directionOff = 'right';
			break;
			case 'su':
			directionOn = 'down';
			directionOff = 'up';
			break;
			case 'sd':
			directionOn = 'up';
			directionOff = 'down';
			break;
			default:
			directionOn = 'right';
			directionOff = 'left';
		}
		
		return {directionOn: directionOn, directionOff: directionOff};
	}

