		$('#video_468105').val(1);
		videoVloseTimeOut_468105 = setTimeout("$('#videoPlayerClose_468105" + num + "').html('X')",2500);
	}
	
	function closePlayer_468105(num){
		$('#videoPlayer_468105' + num).fadeOut(200);
		$('#video_468105').val(0);
		var video = $('#VideoPlayback_468105' + num).attr("src");
		$('#VideoPlayback_468105' + num).attr("src","");
		$('#VideoPlayback_468105' + num).attr("src",video);
		reRunSlideshow_468105();
	}
	
	function getVideoiframeSrc_468105(num){
		var iframeSrc;
		if($('.Videoiframe_468105' + num).length){
			iframeSrc = $('.Videoiframe_468105' + num).attr('src');
			var n = iframeSrc.indexOf("?");
			if(n != -1){
				$('.Videoiframe_468105' + num).attr('src',iframeSrc + '&wmode=opaque');
			}else{
				$('.Videoiframe_468105' + num).attr('src',iframeSrc + '?wmode=opaque');
			}
			
		}
	}

    
