				}
				$('#dipImage_469479' + nums).hide("slide", { direction: directionOff }, speed);
			}else{
				$('#dipImage_469479' + nums).fadeOut(speed);
			}
			
			if($('#videoShell_469479' + nums).length){
				$('#videoShell_469479' + nums).fadeOut(speed);
			}
			if($('#dipContent_469479' + nums).length){
				$('#dipContent_469479' + nums).fadeOut(speed);
			}
			
			if(nums >= totalDips){nums = 1;}else{nums = nums + 1;}
			
			if($('#nav_469479' + nums).length){
				$('#nav_469479' + nums).addClass('on');
			}
			
			if($('#nav_li_469479' + nums).length){
				$('#nav_li_469479' + nums).addClass('on');
			}
			
			if(slideshowMotion != 'f'){
				$('#dipImage_469479' + nums).show("slide", { direction: directionOn }, speed, function(){
				if($('#videoShell_469479' + nums).length){
					$('#videoShell_469479' + nums).fadeIn(speed);
					}	
				});
			}else{
				$('#dipImage_469479' + (nums)).fadeIn(speed);
			}
			
			if(slideshowMotion == 'f'){
				if($('#videoShell_469479' + nums).length){
					$('#videoShell_469479' + nums).fadeIn(speed);
				}
			}
			if($('#dipContent_469479' + nums).length){
				$('#dipContent_469479' + (nums)).fadeIn(speed);
			}
				
			if(num < totalDips){timer = num + 1;}else{timer = 1;}
			rotateDip_469479(timer);
			document.getElementById('currentNum_469479').value = nums;
		}
	}
	
