			var n = iframeSrc.indexOf("?");
			if(n != -1){
				$('.Videoiframe_469479' + num).attr('src',iframeSrc + '&wmode=opaque');
			}else{
				$('.Videoiframe_469479' + num).attr('src',iframeSrc + '?wmode=opaque');
			}
			
		}
	}
