function openMenu(e,linked,id){
	var x = e.which || e.keyCode;
	//alert(linked + ' - ' + id + ' - ' + x);
	if (x == 13) {
	var url_prefix = document.getElementById('url_prefix').value;
	document.location.href= url_prefix + '/' + linked;
	}
}

