	function switchDip_438633(num,pause){
		clearTimeout(slideshowTimer_438633);
		var timer;
		var nums = num;
		var slideshowMotion = slideshowMotion_438633();
		var speed = slideshowMotionSpeed_438633();
		var currentNum = document.getElementById('currentNum_438633').value;
		var totalDips = document.getElementById('totalDips_438633').value;
		var video = document.getElementById('video_438633').value;
		if(speed == '' || speed == null){
			speed = 800;
		}
		
		var direction = directions_438633(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		
		if(totalDips > 1 && video == 0){
		
			if(nums < 1){nums = 1;}
			if(nums > totalDips){nums = totalDips;}
			
			if($('#nav_438633' + nums).length){
				$('#nav_438633' + nums).removeClass('on');
			}
			
			if($('#nav_li_438633' + nums).length){
				$('#nav_li_438633' + nums).removeClass('on');
			}
			
			if(slideshowMotion != 'f'){
				if($('#videoShell_438633' + nums).length){
					$('#videoShell_438633' + nums).hide();
				}
				$('#dipImage_438633' + nums).hide("slide", { direction: directionOff }, speed);
			}else{
				$('#dipImage_438633' + nums).fadeOut(speed);
			}
			
			if($('#videoShell_438633' + nums).length){
				$('#videoShell_438633' + nums).fadeOut(speed);
			}
			if($('#dipContent_438633' + nums).length){
				$('#dipContent_438633' + nums).fadeOut(speed);
			}
			
			if(nums >= totalDips){nums = 1;}else{nums = nums + 1;}
			
			if($('#nav_438633' + nums).length){
				$('#nav_438633' + nums).addClass('on');
			}
			
			if($('#nav_li_438633' + nums).length){
				$('#nav_li_438633' + nums).addClass('on');
			}
			
			if(slideshowMotion != 'f'){
				$('#dipImage_438633' + nums).show("slide", { direction: directionOn }, speed, function(){
				if($('#videoShell_438633' + nums).length){
					$('#videoShell_438633' + nums).fadeIn(speed);
					}	
				});
			}else{
				$('#dipImage_438633' + (nums)).fadeIn(speed);
			}
			
			if(slideshowMotion == 'f'){
				if($('#videoShell_438633' + nums).length){
					$('#videoShell_438633' + nums).fadeIn(speed);
				}
			}
			if($('#dipContent_438633' + nums).length){
				$('#dipContent_438633' + (nums)).fadeIn(speed);
			}
				
			if(num < totalDips){timer = num + 1;}else{timer = 1;}
			rotateDip_438633(timer);
			document.getElementById('currentNum_438633').value = nums;
		}
	}
	
	function slideshowMotion_438633(){
		if($('#slideshowMotion_438633').length){
			slideshowMotion = document.getElementById('slideshowMotion_438633').value;
