	function reRunSlideshow_504451(sp){
		var currentNum_504451 = document.getElementById('currentNum_504451').value;
		if(sp == 1){
			var time = sp;
		}else{
			var time = document.getElementById('slideshowSpeed_504451').value;
		}
		slideshowTimer_504451 = setTimeout(function(){rotateDip_504451(currentNum_504451);},time);
	}
	
	//slideshow
	function rotateDip_504451(num){
		var time = document.getElementById('slideshowSpeed_504451').value;
		slideshowTimer_504451 = setTimeout("switchDip_504451(" + num + ")",time);
	}
	
	function dipNavigationDelay_504451(nums,speed){
			
			var slideshowMotionSpeed = slideshowMotionSpeed_504451();
			if(speed == '' || speed == null){
				if(slideshowMotionSpeed == '' || slideshowMotionSpeed == null){
					speed = 800;
				}else{
					speed = slideshowMotionSpeed;
				}
			}
			
			if(dipNavTimeVar_504451 == 1){
				dipNavigation_504451(nums,speed);
			}else{
				if(typeof dipNavTime_504451 !== "undefined"){
					clearTimeout(dipNavTime_504451);
				}
				
				dipNavTime_504451 = setTimeout(function(){dipNavigationDelay_504451(nums,speed)},speed);
			}
			timeClear = 1;
	}
	
