	function switchDip_504451(num,pause){
		clearTimeout(slideshowTimer_504451);
		var timer;
		var nums = num;
		var slideshowMotion = slideshowMotion_504451();
		var speed = slideshowMotionSpeed_504451();
		var currentNum = document.getElementById('currentNum_504451').value;
		var totalDips = document.getElementById('totalDips_504451').value;
		var video = document.getElementById('video_504451').value;
		if(speed == '' || speed == null){
			speed = 800;
		}
		
		var direction = directions_504451(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		
		if(totalDips > 1 && video == 0){
		
			if(nums < 1){nums = 1;}
			if(nums > totalDips){nums = totalDips;}
			
			if($('#nav_504451' + nums).length){
				$('#nav_504451' + nums).removeClass('on');
			}
			
			if($('#nav_li_504451' + nums).length){
				$('#nav_li_504451' + nums).removeClass('on');
			}
			
			if(slideshowMotion != 'f'){
				if($('#videoShell_504451' + nums).length){
					$('#videoShell_504451' + nums).hide();
				}
				$('#dipImage_504451' + nums).hide("slide", { direction: directionOff }, speed);
			}else{
				$('#dipImage_504451' + nums).fadeOut(speed);
			}
			
			if($('#videoShell_504451' + nums).length){
				$('#videoShell_504451' + nums).fadeOut(speed);
			}
			if($('#dipContent_504451' + nums).length){
				$('#dipContent_504451' + nums).fadeOut(speed);
			}
			
			if(nums >= totalDips){nums = 1;}else{nums = nums + 1;}
			
			if($('#nav_504451' + nums).length){
				$('#nav_504451' + nums).addClass('on');
			}
			
			if($('#nav_li_504451' + nums).length){
				$('#nav_li_504451' + nums).addClass('on');
			}
			
			if(slideshowMotion != 'f'){
				$('#dipImage_504451' + nums).show("slide", { direction: directionOn }, speed, function(){
				if($('#videoShell_504451' + nums).length){
					$('#videoShell_504451' + nums).fadeIn(speed);
					}	
				});
			}else{
				$('#dipImage_504451' + (nums)).fadeIn(speed);
			}
			
			if(slideshowMotion == 'f'){
				if($('#videoShell_504451' + nums).length){
					$('#videoShell_504451' + nums).fadeIn(speed);
				}
			}
			if($('#dipContent_504451' + nums).length){
				$('#dipContent_504451' + (nums)).fadeIn(speed);
			}
				
			if(num < totalDips){timer = num + 1;}else{timer = 1;}
			rotateDip_504451(timer);
			document.getElementById('currentNum_504451').value = nums;
		}
	}
	
	function slideshowMotion_504451(){
		if($('#slideshowMotion_504451').length){
			slideshowMotion = document.getElementById('slideshowMotion_504451').value;
		}else{
			slideshowMotion = 'f';
		}
		if(slideshowMotion == ''){slideshowMotion = 'f'}
		return slideshowMotion;
	}
	
	function slideshowMotionSpeed_504451(){
		if($('#slideshowMotionSpeed_504451').length){
			slideshowMotionSpeed = document.getElementById('slideshowMotionSpeed_504451').value;
		}else{
			slideshowMotionSpeed = '';
		}
		return slideshowMotionSpeed;
	}
	
	
	function slideshowQuickNavSpeed_504451(){
		if($('#slideshowQuickNavSpeed_504451').length){
			slideshowQuickNavSpeed = document.getElementById('slideshowQuickNavSpeed_504451').value;
		}else{
			slideshowQuickNavSpeed = 50;
		}
	
		return slideshowQuickNavSpeed;
	}
	
	//motion direction
	function directions_504451(slideshowMotion){
		switch(slideshowMotion){
			case 'sr':
			directionOn = 'left';
			directionOff = 'right';
			break;
			case 'su':
			directionOn = 'down';
			directionOff = 'up';
			break;
			case 'sd':
			directionOn = 'up';
			directionOff = 'down';
			break;
			default:
			directionOn = 'right';
			directionOff = 'left';
		}
		
		return {directionOn: directionOn, directionOff: directionOff};
	}

	//video
	function playvideo_504451(num){
		getVideoiframeSrc_504451(num);
		$('#videoPlayerClose_504451' + num).html('Close X');
		$('#videoPlayer_504451' + num).fadeIn(800);
		$('#video_504451').val(1);
		videoVloseTimeOut_504451 = setTimeout("$('#videoPlayerClose_504451" + num + "').html('X')",2500);
	}
	
	function closePlayer_504451(num){
		$('#videoPlayer_504451' + num).fadeOut(200);
		$('#video_504451').val(0);
		var video = $('#VideoPlayback_504451' + num).attr("src");
		$('#VideoPlayback_504451' + num).attr("src","");
		$('#VideoPlayback_504451' + num).attr("src",video);
		reRunSlideshow_504451();
	}
	
	function getVideoiframeSrc_504451(num){
		var iframeSrc;
		if($('.Videoiframe_504451' + num).length){
			iframeSrc = $('.Videoiframe_504451' + num).attr('src');
			var n = iframeSrc.indexOf("?");
			if(n != -1){
				$('.Videoiframe_504451' + num).attr('src',iframeSrc + '&wmode=opaque');
			}else{
				$('.Videoiframe_504451' + num).attr('src',iframeSrc + '?wmode=opaque');
			}
			
		}
	}
