            $(document).ready(function() {
				if ($('.white-popup').length) {
					$('.slide-pop').magnificPopup({
					  disableOn: 700,
					  type: 'inline',
					  midClick: true,
					  mainClass: 'mfp-fade',
					  removalDelay: 160,
					  preloader: false,
					  fixedContentPos: false
					});
				}
                $('.main-slider').slick({
                    dots: true,
                    infinite: true,
                    adaptiveHeight: true, 
                    autoplay: true,
                    autoplaySpeed: '4000',
                    arrows: false,
                    appendDots: '.slideNav'
                });
            });
