	function switchDip_387202(num,pause){
		clearTimeout(slideshowTimer_387202);
		var timer;
		var nums = num;
		var slideshowMotion = slideshowMotion_387202();
		var speed = slideshowMotionSpeed_387202();
		var currentNum = document.getElementById('currentNum_387202').value;
		var totalDips = document.getElementById('totalDips_387202').value;
		var video = document.getElementById('video_387202').value;
		if(speed == '' || speed == null){
			speed = 800;
		}
		
		var direction = directions_387202(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		
		if(totalDips > 1 && video == 0){
		
			if(nums < 1){nums = 1;}
			if(nums > totalDips){nums = totalDips;}
			
			if($('#nav_387202' + nums).length){
				$('#nav_387202' + nums).removeClass('on');
			}
			
			if($('#nav_li_387202' + nums).length){
				$('#nav_li_387202' + nums).removeClass('on');
			}
			
			if(slideshowMotion != 'f'){
				if($('#videoShell_387202' + nums).length){
					$('#videoShell_387202' + nums).hide();
				}
				$('#dipImage_387202' + nums).hide("slide", { direction: directionOff }, speed);
			}else{
				$('#dipImage_387202' + nums).fadeOut(speed);
			}
			
			if($('#videoShell_387202' + nums).length){
				$('#videoShell_387202' + nums).fadeOut(speed);
			}
			if($('#dipContent_387202' + nums).length){
				$('#dipContent_387202' + nums).fadeOut(speed);
			}
			
			if(nums >= totalDips){nums = 1;}else{nums = nums + 1;}
			
			if($('#nav_387202' + nums).length){
				$('#nav_387202' + nums).addClass('on');
			}
			
			if($('#nav_li_387202' + nums).length){
				$('#nav_li_387202' + nums).addClass('on');
			}
			
			if(slideshowMotion != 'f'){
				$('#dipImage_387202' + nums).show("slide", { direction: directionOn }, speed, function(){
				if($('#videoShell_387202' + nums).length){
					$('#videoShell_387202' + nums).fadeIn(speed);
