	if (!String.prototype.isInList) {
	   String.prototype.isInList = function() {
		  let value = this.valueOf();
		  for (let i = 0, l = arguments.length; i < l; i += 1) {
			 if (arguments[i] === value) return true;
		  }
		  return false;
	   }
	}

	function link_is_external(link_element) {
		//alert(link_element.host);
		if(link_element.host != '' && link_element.host != 'www.kaukauna.k12.wi.us' && link_element.host.isInList('kaukauna.cms4schools.net,kaukauna.k12.wi.us,www.kaukauna.k12.wi.us')== false){
		return (link_element.host !== window.location.host);
		}else{
			return false;
		}
	}
	
	$(document).ready(function() {
		
		$('a').click(function(){
		if (link_is_external(this)) {
		   if (confirm("You're leaving this site and going to an external site. To continue click OK.") == true) {
				return true;
			} else {
				return false;
			}
		}
		});

	});
