// add active to nav menus
$("#menucontainer ul li a").filter(function(){
    return this.href == location.href.replace(/#.*/, "");
}).parent().addClass("active");

// same for footer
$("#foot-nav li a").filter(function(){
    return this.href == location.href.replace(/#.*/, "");
}).addClass("active").removeAttr("href");

// for full size mobile nav
$(document).ready(function(){
	resizeDiv();
});
window.onresize = function(event) {
	resizeDiv();
}
function resizeDiv() {
	vpw = $(window).width();
	vph = $(window).height();

	if (vpw >= 1000) {
		$('.topBar').css({'display': 'block'});
		$('.topBarOffset').css({'display': 'block'});
	}
}

//  mobile nav buttons
$('.open-button').click(function() {
	$('.navigation #menucontainer').fadeIn('100');
	$('body').css({'overflow': 'hidden'});
	$('.topBar').css({'display': 'none'});
	$('.topBarOffset').css({'display': 'none'});
});
$('.close-button').click(function() {
	$('.navigation #menucontainer').fadeOut('100');
	$('body').css({'overflow': 'visible'});
	$('.topBar').css({'display': 'block'});
	$('.topBarOffset').css({'display': 'block'});
});
