  	function SortByDate(a, b){
  		return new Date(a.SortDate) - new Date(b.SortDate);
  	}

      // Advanced RenderEvents function which incorporates setting event days to the appropriate date, as well as adding in dates to fill the 5 day slot.
      function RenderEvents(eventArray){
      try {
              jQuery('.dashboardEvents.events_list').html("");
              var MaxItemCount = 5;

              if(eventArray.length > 0) {
                  var FinishedEventArray = new Array();
                  var eventCount = 0;
                  var todaysDate = new Date();

                  // Prepare events
                  for(var i=0; i< eventArray.length; i++) {
                      // Convert string dates to actual date objects
                      eventArray[i]["Dates"] = getDateObject(eventArray[i].SortDate, eventArray[i].EndDate);
                      // If the event started in the past, set the day's date to today
                      if(eventArray[i].Dates.from < todaysDate) {
                          eventArray[i].Dates.from = todaysDate;
                      }
                  }

                  var openSpots = MaxItemCount - eventArray.length;
                  var currentEventIndex = 0;
                  var foundMatch = false;

                  if(openSpots > 0) {
                      var currentDate = event[0].Dates.from;
                      var finished = false;

                      while(!finished) {
                          foundMatch = false;
                          for(var i=0; i 0 && isDateIn(eventArray[i].Dates.from, eventArray[i].Dates.to, currentDate)) {
                                  foundMatch = true;
                                  openSpots--;
                                  var newEvent = jQuery.extend(true, {}, eventArray[i]);
                                  newEvent.Dates.from = currentDate;
                                  FinishedEventArray.push(newEvent);
                              } 
                          }

                          if(foundMatch) {
                              currentDate.setDate(currentDate.getDate()+1);
                          } else if (currentEventIndex < eventArray.length) {
                              currentEventIndex++;
                              currentDate.setDate(eventArray[currentEventIndex].Date.from);
                          } else {
                              finished = true;
                          }

                          if(FinishedEventArray.length == MaxItemCount) {
                              finished = true;
                          }

                      }
                  } else {
                      for(var i=0; i<MaxItemCount; i++){
                          FinishedEventArray.push(eventArray[i]);
                      }
                  }
                  for(var i=0; i< FinishedEventArray.length; i++) {
                      var eventTitle = "";
  			        if (FinishedEventArray[i].Title) {	
  				        eventTitle =  FinishedEventArray[i].Title;
