" + getWeekdayDoWOnly(FinishedEventArray[i].Dates.from.getDay()) + "");
  					    newEvent.append( "
" + (parseInt(FinishedEventArray[i].Dates.from.getMonth())+1)+"/"+FinishedEventArray[i].Dates.from.getDate() + "");
  					    newEvent.append( "
" +  (FinishedEventArray[i].StartTime != FinishedEventArray[i].EndTime ? FinishedEventArray[i].StartTime + " - " + FinishedEventArray[i].EndTime:"") + "");
  					    newEvent.append( "
›");
  				    newEventWrapper.append( newEvent);
  			    jQuery('.dashboardEvents.events_list').append( newEventWrapper);
                  }
              }

              }  catch(err) {
  			console.log(err);
              // if this fails, just do old method
              jQuery('.dashboardEvents.events_list').html("");
  		    //for(i = 0; i < eventArray.length; i++)
