      // dateFrom in MM/DD/YY, dateTo in MM/DD, CheckDate as Date Object
      function getDateObject(dateFrom, dateTo) {


          var d1 = dateFrom.split("/");
          d1[2] = "20"+d1[2];

          var d2 = dateTo.split("/");
          if(parseInt(d2[0]) < parseInt(d1[0])) {
              dateTo = dateTo + "/"+padToTwo(parseInt(d1[2])+1);
          } else {
              dateTo = dateTo + "/"+padToTwo(parseInt(d1[2]));
          }
          d2 = dateTo.split("/");

          var fromTime = new Date(d1[2], d1[0]-1, d1[1]);  // -1 because months are from 0 to 11
          var toTime   = new Date(d2[2], d2[0]-1, d2[1]);

          return {from: fromTime, to: toTime};
      }

      function isDateIn(from, to, check) {
          return (check >= from && check <= to)
      }

      function padToTwo(number) {
          if (number<=99) { number = ("0"+number).slice(-2); }
          return number;
      }

