  	function getWeekday(indate){
  		var days = ["Sun","Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];
  		var dVal = new Date(Date.parse(indate));
  		var dayIndex = dVal.getDay();
  		return days[dayIndex];
  	}
      function getWeekdayDoWOnly(DoW){
  		var days = ["Sun","Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];
  		return days[DoW];
  	}

