jQuery(window).load(function(e){
  if(jQuery.cookie('openLunchTab') && jQuery.cookie('openLunchTab').length > 0) {
    setLunchTab(jQuery(jQuery.cookie('openLunchTab')+".schoolLunchTab"));
  }
  else {
    setLunchTab(jQuery('#lunch_2.schoolLunchTab'));
  }

  jQuery('#schoolLunchTabs .schoolLunchTab').click(function(){
    setLunchTab(jQuery(this));
  });
});

