	function switchDip_428250(num,pause){
		clearTimeout(slideshowTimer_428250);
		var timer;
		var nums = num;
		var slideshowMotion = slideshowMotion_428250();
		var speed = slideshowMotionSpeed_428250();
		var currentNum = document.getElementById('currentNum_428250').value;
		var totalDips = document.getElementById('totalDips_428250').value;
		var video = document.getElementById('video_428250').value;
		if(speed == '' || speed == null){
			speed = 800;
		}
		
		var direction = directions_428250(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		
		if(totalDips > 1 && video == 0){
		
			if(nums < 1){nums = 1;}
			if(nums > totalDips){nums = totalDips;}
			
			if($('#nav_428250' + nums).length){
				$('#nav_428250' + nums).removeClass('on');
			}
			
			if($('#nav_li_428250' + nums).length){
				$('#nav_li_428250' + nums).removeClass('on');
			}
			
			if(slideshowMotion != 'f'){
				if($('#videoShell_428250' + nums).length){
					$('#videoShell_428250' + nums).hide();
				}
				$('#dipImage_428250' + nums).hide("slide", { direction: directionOff }, speed);
			}else{
				$('#dipImage_428250' + nums).fadeOut(speed);
			}
			
			if($('#videoShell_428250' + nums).length){
				$('#videoShell_428250' + nums).fadeOut(speed);
			}
			if($('#dipContent_428250' + nums).length){
				$('#dipContent_428250' + nums).fadeOut(speed);
			}
			
			if(nums >= totalDips){nums = 1;}else{nums = nums + 1;}
			
			if($('#nav_428250' + nums).length){
				$('#nav_428250' + nums).addClass('on');
			}
			
			if($('#nav_li_428250' + nums).length){
				$('#nav_li_428250' + nums).addClass('on');
			}
			
			if(slideshowMotion != 'f'){
				$('#dipImage_428250' + nums).show("slide", { direction: directionOn }, speed, function(){
				if($('#videoShell_428250' + nums).length){
					$('#videoShell_428250' + nums).fadeIn(speed);
					}	
				});
			}else{
				$('#dipImage_428250' + (nums)).fadeIn(speed);
			}
			
			if(slideshowMotion == 'f'){
				if($('#videoShell_428250' + nums).length){
					$('#videoShell_428250' + nums).fadeIn(speed);
				}
			}
			if($('#dipContent_428250' + nums).length){
				$('#dipContent_428250' + (nums)).fadeIn(speed);
			}
				
			if(num < totalDips){timer = num + 1;}else{timer = 1;}
			rotateDip_428250(timer);
			document.getElementById('currentNum_428250').value = nums;
		}
	}
	
	function slideshowMotion_428250(){
		if($('#slideshowMotion_428250').length){
			slideshowMotion = document.getElementById('slideshowMotion_428250').value;
		}else{
			slideshowMotion = 'f';
		}
		if(slideshowMotion == ''){slideshowMotion = 'f'}
		return slideshowMotion;
	}
	
	function slideshowMotionSpeed_428250(){
