		return slideshowQuickNavSpeed;
	}
	
	//motion direction
	function directions_428250(slideshowMotion){
		switch(slideshowMotion){
			case 'sr':
			directionOn = 'left';
			directionOff = 'right';
			break;
			case 'su':
			directionOn = 'down';
			directionOff = 'up';
			break;
			case 'sd':
			directionOn = 'up';
			directionOff = 'down';
			break;
			default:
			directionOn = 'right';
			directionOff = 'left';
		}
		
		return {directionOn: directionOn, directionOff: directionOff};
	}

